css:
@-moz-document domain(evonomics.com)
{
  
  body, .us_wrapper.us_skin_minimal .us_button,.us_wrapper.us_skin_minimal .us_fan_count { background-color: #222; color: #999 }

#conversation, .entry-meta a { background-color: #222 !important; color: #999 !important; }

.entry-title a, .single .page-title, .site-content .site-navigation a, .page-title { color: #BBB }

.widget-title, .widget-title a, .sidebarsingle .us_share_text_span, .shortcode-author-avatars div.author-list .user .name { color: #BBB !important; }

.header-wrapper h2, h1, h2, h3, h4, h5, h6, .entry-meta, #text-5 h2 { color: #666 }

.hentry { border-color: #191919 }

.optshorts { background-color: #333; }

.relpost-block-single { background-color: #444; }

.relpost-block-single:hover { background-color: #666; }

input, input[type=text],input[type=email],textarea { background-color: #666; border-color: #666 }
  
 
  
}
