css:
@-moz-document domain("send.firefox.com") {
.bg-white {
    background-color: #25272a;
}

.md\:bg-grey-lightest, .bg-grey-10 {
    background-color: #333;
}

.main > section {
    background-color: #25272a;
    color: #c0c0c0;
}

a {
    color: #fff;
}

.font-bold {

    font-weight: 700;
    color: #fff;

}

.text-grey-darkest, .sm\:text-base {

    color: #c0c0c0;

}

.md\:bg-grey-10 {
    background: #333
}

.bg-grey-lightest {

    background-color: #333;
    color: #c0c0c0;

}

body {
    background: transparent;
    background-position: 50%;
    background-repeat: no-repeat;
    background-size: cover;
    overflow-x: hidden;
}

.mozilla-logo {
    filter: invert(1)
}

.text-grey-darker {
    color: #c0c0c0;
}

.shadow-light {
    box-shadow: 0 0 8px 0 #222;
}

*, ::after, ::before {
    border: 0 solid #222;
}

.md\:justify-end:after {
    content: ' | Firefox Send Dark v1.1.0 by sks316' !important;
    color: #c0c0c0;
}
}
