css:
@-moz-document domain("informatics.msk.ru") {

body {

    background-color: #242628

}

#header-home {

    background-color: black;

    background-image: url(https://psv4.userapi.com/c848220/u160494057/docs/d9/fdfcc403d329/logo2.png?extra=dRYlk9F9cOK_ziBkEurXEJFhK3tywfbIYToladOlCw7Xq7ZDiI15ntREHjlVQqZUJiAHdJQYTW0io2JAge61fog-1yRNc1OdlXKeh2Icrpnr93WXm3KTyfa0wW8rbInP_GsMioiiyAzmRWfPBZRrcjaLuw);

    BACKGROUND-POSITION: 10px 10px;

    background-repeat: no-repeat;

    border-color: white;

}

select {

    border-radius: 10px;

    border-color: white;

    color: white;

    background-color: black;

    font-family : franklin gothic medium !important;

}

input {

    padding:10px 33px !important;

    position: center;

}

.ui-tabs .ui-tabs-panel {

    display: block;

    border-width: 0;

    padding: 1em 1.4em;

    background: black;

}

.bootstrap .well {

    min-height: 20px;

    padding: 19px;

    margin-bottom: 20px;

    background-color: #000000;

    border: 1px solid #e3e3e3;

    -webkit-border-radius: 4px;

    -moz-border-radius: 4px;

    border-radius: 4px;

    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);

    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);

    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);

}

.bootstrap h5 {

    color: white;

}

.ui-widget-content {

    border: 1px solid #e0cfc2;

    /*background: black url(images/ui-bg_inset-soft_100_ffffff_1x100.png) 50% bottom repeat-x !important; */

    color: #ffffff !important;

}

.ui-widget-header {

    background: black !important;

}

.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {

    color: #000000 !important;

    text-decoration: none;

}

input {

    -webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.08);

    -moz-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.08);

    box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.08);

    color: #fff;

    display:block;

    font-family : franklin gothic medium !important;

    background-color: #525252;

    text-align: center;

    font-family: Arial, Helvetica, sans-serif;

    font-size: 14px;

    padding: 8px 16px;

    margin: 10px auto;

    text-decoration: none;

    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.075);

    -webkit-transition: background-color 0.1s linear;

    -moz-transition: background-color 0.1s linear;

    -o-transition: background-color 0.1s linear;

    transition: background-color 0.1s linear;

}

.tabrow0 {

    display: none;

}

.navbar {
	
	background: none;
    background-color: black;

    border-color: white;

    /* height: 20px; */

   

}

.generalbox {

    border-color: black;

    background-color: #16191b;

}

.sideblock .header {

    border-color: black;

    background-color: #16191b;

}

.sideblock .content {

    border-color: black;

    background-color: #16191b;

}

h2 {

    border-color: white;

    background-color: #16191b;

    color: white;

    font-family : franklin gothic medium !important;

}

body, td, li {

    color: white;

}

a:link {

    color: lightgray;

}

a:visited {

    color: #ffffff;

}

h1.headermain {

    display:none;

}

body, td, li {

    font-family : franklin gothic medium !important;

}

.tree li a.node {

    display: inline-block;

    border: 1px solid #ccc;

    padding: 3px 5px;

    text-decoration: none;

    color: #666;

    font-family : franklin gothic medium !important;

    font-size: 20px !important;

    border-radius: 3px;

    -webkit-border-radius: 3px;

    -moz-border-radius: 3px;

}

a.show_all_button {

    display: none;

}

.tree li a.node {

    display: inline-block;

    border: 1px solid #fff;

    color: lightgrey;

}

a img {

    display: none;

    border: none;

}

h1 {

    background: url(https://psv4.userapi.com/c848220/u160494057/docs/d9/fdfcc403d329/logo2.png?extra=dRYlk9F9cOK_ziBkEurXEJFhK3tywfbIYToladOlCw7Xq7ZDiI15ntZKHjlVQqZUJiAHdJQYTW0io2JAge61fog-1yRNc1OdlXKeh2Icrpnr93WXm3KTyfa0wW8rbInP_GsMioiiyFngFmDID8dqIWXXsQ) no-repeat bottom;

}
}

@-moz-document url-prefix("https://informatics.msk.ru/course") {
#header {
    background-color: black;
    border-color: white;
}
.navbar {
    background-color: black;
    border-color: white;
    /* height: 20px; */
	background: none;
    
}
#course-view .section.main .content {
    border-color: black;
    background-color: #16191b;
}
h1 {
    background: none;
    background-color: black;
    color: white;
    font-family : franklin gothic medium !important;
}
h3 {
    background: none;
    background-color: black;
    color: white;
    font-family : franklin gothic medium !important;
    border-color: white;
}
#course-view .section.main .side {
    border-color: black;
    background-color: #16191b;
}
}

@-moz-document url-prefix("https://informatics.msk.ru/user") {
#header {
    background-color: black;
    background-image: url(https://psv4.userapi.com/c848220/u160494057/docs/d9/fdfcc403d329/logo2.png?extra=dRYlk9F9cOK_ziBkEurXEJFhK3tywfbIYToladOlCw7Xq7ZDiI15ntREHjlVQqZUJiAHdJQYTW0io2JAge61fog-1yRNc1OdlXKeh2Icrpnr93WXm3KTyfa0wW8rbInP_GsMioiiyAzmRWfPBZRrcjaLuw);
    BACKGROUND-POSITION: 10px 10px;
    background-repeat: no-repeat;
    border-color: white;
}
}

@-moz-document url-prefix("https://informatics.msk.ru/mod/statements") {
.statements_chapter_title {
    display: none !important;
}
#header {
    background-color: black;
    background-image: url(https://psv4.userapi.com/c848220/u160494057/docs/d9/fdfcc403d329/logo2.png?extra=dRYlk9F9cOK_ziBkEurXEJFhK3tywfbIYToladOlCw7Xq7ZDiI15ntREHjlVQqZUJiAHdJQYTW0io2JAge61fog-1yRNc1OdlXKeh2Icrpnr93WXm3KTyfa0wW8rbInP_GsMioiiyAzmRWfPBZRrcjaLuw);
    BACKGROUND-POSITION: 10px 10px;
    background-repeat: no-repeat;
    border-color: white;
}
img.bigicon {
    display: none !important;
}
input {
    padding: 0px 0px!important;
    position: center;
}
.problem-statement .sample-tests pre {
    background-color:#2b282b;
}
Tr.Caption {
    background-color: #2b282b;
    text-align: center;
    vertical-align: middle;
}
.pagination a {
    text-decoration: none;
    border: solid 1px white;
}
#Searchresult {
    border: solid 1px white;
}
.pagination .current {
    background: lightgray;
    color: #000;
    border: solid 1px white;
}
table {
    display: table;
    border-collapse: separate;
    border-spacing: 2px;
    border-color: white;
}
}
