css:
@-moz-document domain("www.wonderfulsubs.com") {
/*hide recommended sidebar & episode name in remote*/
.recommended-content, .modal .modal-footer {
    display: none !important;
}

/*expand width of video*/
.video-content {
    width: 100% !important;
}

/*move remote to bottom right*/
#site-remote, .z-depth-1.mini-mode-remote {
    width: 250px !important;
    right: 0px;
    left: auto;
    top: auto;
}

/*lessen shadow of remote*/
.modal {
    box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.3);
}

/*lessen padding in remote*/
.modal-content {
    padding: 20px 0px !important;
}

/*the overlay usually covers whole screen and has a click to dismiss remote action. (you can't do anything with remote open)
this makes it invisible but it still covers the remote button so you can click it again to dismiss*/
.modal-overlay {
    left: auto;
    top: auto;
    
    right: 100px;
    height: 50px;
    width: 50px;
    bottom: 2px;
    
    cursor: pointer;
    opacity: 1 !important;
    background: none; /*set a background to see the hitbox*/
}

/*move remote window up*/
#site-remote {
    bottom: 60px !important;
}
}
