css:
@-moz-document domain("downloads.khinsider.com") {
  body, #main, #faux {
      background: #0f0f0f;
  }
  
  a, p, b, h1, h2, h3 {
      font-family: Tahoma, arial, sans-serif;
  }
  
  #faux {
      border-style: solid;
      border-width: 1px;
      border-color: #FF6A00;
  }
  
  #lmenu, #rmenu, #faux {
      padding: 0;
      background: #1f1f1f;
      color: #e5e5e5;
  }
  
  p font {
      color: #FF6A00;
      font-weight: bold;
      font-style: italic;
  }
  
  #lmenu a, #rmenu a, b {
      color: #FFF;
  }
  
  a, h3, .mainlevel {
      color: #FF6A00;
  }
  
  span.pagenav:last-child {
      background-color: #FF6A00;
      border-radius: 5px;
      color: #FFF;
  }
  
  #rmenu .moduletable h3 {
      background: url('https://orig00.deviantart.net/f7b2/f/2019/082/8/2/moduleheadr_by_craftyinventor-dd2syw3.gif') no-repeat left;
  }
  
  #lmenu .moduletable h3 {
      background: url('https://orig00.deviantart.net/6a71/f/2019/082/9/d/modulehead_by_craftyinventor-dd2sz17.gif') no-repeat right;
  }
  
  .moduletable td {
      background: url('https://orig00.deviantart.net/9a73/f/2019/082/c/e/key_by_craftyinventor-dd2syw4.gif') no-repeat left;
  }
  
  h2 {
      color: #FF6A00;
      font-weight: bold;
      font-style: italic;
  }
  
  p b {
      font-size: 0.95em;
  }
  
  #EchoTopic p a {
      color: #FFF !important;
  }
  
  audio:hover, audio:focus, audio:active {
      -webkit-box-shadow: 15px 15px 20px rgba(0, 0, 0, 0.4);
      -moz-box-shadow: 15px 15px 20px rgba(0, 0, 0, 0.4);
      box-shadow: 15px 15px 20px rgba(0, 0, 0, 0.4);
      -webkit-transform: scale(1.05);
      -moz-transform: scale(1.05);
      transform: scale(1.05);
  }
  
  audio {
      -webkit-transition:all 0.35s linear;
      -moz-transition:all 0.35s linear;
      -o-transition:all 0.35s linear;
      transition:all 0.35s linear;
      -moz-box-shadow: 2px 2px 4px 0px #FF6A00;
      -webkit-box-shadow: 2px 2px 4px 0px #FF6A00;
      box-shadow: 2px 2px 4px 0px #FF6A00;
      -moz-border-radius: 35px;
      -webkit-border-radius: 35px;
      border-radius: 35px;
  }
  
  table #songlist tbody :hover:not(th):not(td):not(#songlist_header):not(#songlist_footer){
      transition: all linear;
      transition-duration: 0s;
      font-size: 1.1em;
      color: #FF6A00;
  }
  
  a:hover:not(img):not(th){
      text-overflow: clip;
      white-space: nowrap;
      -webkit-box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.4);
      -moz-box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.4);
      box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.4);
      font-size: 1.1em;
  }
  
  a:not(img):not(th){
      text-overflow: clip;
      -webkit-transition:all 0.35s linear;
      -moz-transition:all 0.35s linear;
      -o-transition:all 0.35s linear;
      transition:all 0.35s linear;
      -moz-box-shadow: 0px 0px 0px 0px #FF6A00;
      -webkit-box-shadow: 0px 0px 0px 0px #FF6A00;
      box-shadow: 0px 0px 0px 0px #FF6A00;
      -moz-border-radius: 35px;
      -webkit-border-radius: 35px;
      border-radius: 35px;
  }
  
  div .user_category_click {
      background-color: #1f1f1f;
      border-style: solid;
      border-width: 1px;
      border-color: #FF6A00;
      color: #FF6A00;    
  }
  
  .album_category_darker {
      border-color: #FFF !important;
      background-color: #FF6A00 !important;
      color: #FFF !important;   
  }
  
  #main #cont {
      height: 100%;
      padding: 0;
      border-style: solid;
      border-width: 1px;
      border-color: #FF6A00;
      border-radius: 5px;
  }
  
  input, textarea, select {
      background-color: #0f0f0f;
      border-color: #FF6A00;
      border-width: 1px;
      color: #FFF;
      border-radius: 3px;
      margin-top: 2px;
      margin-bottom: 2px;
  }
  
  table tbody tr th, #songlist .plSel, table tbody tr th b {
      background-color: #FF6A00 !important;
      color: #FFF !important;
      font-size: 1em !important;
  }
  
  table #songlist tbody tr :not(.plSel) {
      background-color: #1f1f1f;
      color: #FFF;
      border: 0;
  }
  
  .audioplayer {
      background: #000;
  }
  
  .audioplayer div {
      border: none;
  }
  
  .audioplayer i {
      color: #FF6A00;
  }
  
  .audioplayer div :hover :not(.audioplayerVolumeBar) :not(.audioplayerVolumeBarLevel) {
      background-color: #1f1f1f;
  }
  
  .audioplayerBarPlayed {
      background: #FF6A00
  }
  
  .audioplayerVolumeBar {
      background: #000;   
  }
  
  .audioplayerVolumeBar .audioplayerVolumeBarLevel {
      background: #FF6A00;   
  }
  
  #songlist .plSel a {
      color: #FFF;
  }
  
  #delta {
      background: #000 url('https://orig00.deviantart.net/1a4e/f/2019/082/4/3/bottom_by_craftyinventor-dd2t0fo.gif') repeat-x left;
      position: relative;
      left:-3px;
      width:100.7%;
  }
  
  #audioplayerButtons a[id^="btn"] {
      color: #FF6A00;
  }
  
  #audioplayerButtons a[id^="btn"]:hover {
      background-color: #FF6A00;
      border-color: rgba(255, 106, 0, 100);
      color: #FFF;
  }
}
