css:
@-moz-document url-prefix(https://kanbanflow.com/board/) {
	#board-table {
    background-color: #515151;
    border-collapse: collapse;
}
#header {
    background-color: #36B345
}
.boardHeader-shortcut, .boardHeader-menuButton {
    background-color: #eaeaea
}
.clickable {
    color: black;
    background-color:#eaeaea;
    border-radius: 4px;
    padding: 1px 6px
}
.boardHeader, .board-table td {
    background-color: #515151
}
.columnHeader-inner {
    background-color: #cccccc
}
.board-taskListCell {
    border-top:1px solid #cccccc;
    border-left:1px solid #cccccc;
}
.columnHeader {
    border-top:1px solid #cccccc;
    border-left:1px solid #cccccc;
}
.board-wrapper-inner {
    padding:0 15px 15px;
    background-color: #cccccc
}
.collapsed-info-title {
    color: #cccccc
}
body {
    font-family:"Fira Sans", arial, sans-serif;
}
.task-name {
    font-size: 16px;
}
.columnHeader-name {
    font-size: 18px;
}
#workTimerDialog {
    background-color: #cccccc;
}
.workTimerDialog-clockWrapper div {
    color: black
}
.workTimerDialog-toolbarItem-label {
    background-color: #515151;
}
.workTimerDialog-infoWrapper div {
    color: black
}
.workTimerDialog-clockButton {
    background-color:#36B345;
}
}
