css:
@-moz-document domain(sparksnotes.com) {
body {
    background: #222;
    color: #fff;
}

h1, h2, h3, h4, h5, h6,
.letter-list__headline h1,
.subnav__bold a,
.expanded-container-outer .category,
#characterlist .content_txt {
    color: #fff;
}

.expanded-container-outer .category:hover, .quotation {
    color: #ccc;
}

p,
.title-nav ul .title-nav__item > a,
#site-breadcrumbs li:last-child a,
.page-link .page-link--inner {
    color: #fff;
}

.arrow-nav-item:after,
#site-breadcrumbs li:not(:last-child):after {
    border-top: 4px solid #fff;
}

.global-header img {
    filter: invert(90%) hue-rotate(180deg);
}

.header-space {
    display: none;
}

.bn-wrapper,
.expanded-container-outer,
.title-nav,
.global-header,
.global-header:after,
.global-header:before {
    border-color: #555;
}

.page-link--inner:after, .page-link--inner:before {
    content: none !important;
}

.footer {
    background-color: #2a2a2a;
    color: #fff;
}

.footer p {
    color: #fff;
}

.title-nav {
    background: transparent;
    border-color: transparent;
}

.paginate-link {
    background: #444;
    color: #26a1ff;
}

.paginate-link:hover,
.paginate-link--active {
    background: #26a1ff;
}

.global-header li a,
.expanded-container-outer .indented a,
.container a,
.content-container a,
a,
.footer a,
#site-breadcrumbs .breadcrumb {
    color: #26a1ff;
    text-decoration: none;
}

.right_rail,
.social-unit--interior {
    display: none;
}

.interior-container,
#section {
    max-width: none;
    width: auto;
}

.content--interior {
    max-width: 900px;
}

.global-header {
    background: transparent !important;
}
}
