css:
@-moz-document domain("animeshow.tv") {
* {
    box-sizing: border-box;
    box-shadow: none!important;
    border: 0px solid transparent;
    background-color: transparent;
}

#nav.affix {
    z-index: 0;
}
.img_bg img {
    margin: -185px 0 0 225px!important;
}
.img_bg2 img {
    margin: -238px 0 0 0!important;
}

body {
    line-height: normal;
    overflow-x: hidden;
}
@media screen and (min-width: 768px) {
    .container {
        width: 100% !important;
        max-width: 100%;
    }
}
.col-md-9 {
    width: 100%;
}

.container {
    padding-right: 0px;
    padding-left: 0px;
}
.main .row {
    /*no grey bg/thick flax border around containers*/
    background-color: transparent;
    padding-top: 0px;
}

.row {
    margin-right: 0px;
    margin-left: 0px;
}

#p_a .row > div,
h1 {
    border-bottom: transparent!important;
}

/* Ads */
.v15fwhov,
.row div:nth-child(5),
#latest_anime iframe {
    display: none;
}

#p_a .row,
/* Popular Anime */
#latest_anime .row,
/* New Anime Eps */
#sb_c ul/* Sidebar */
{
    display: flex;
    flex-wrap: wrap;
}

#p_a .row > div {
    flex: 1 0 10%;
}

#latest_anime .row > div {
    flex: 1 0 20%;
}


/* Sidebar Section */
.col-md-3,
.col-sm-4 {
    width: 100%;
}
#sb_c {
    width: 99.2%;
    margin: 0 0 0 10px;
}

#sb_c ul li {
    flex: 1 0 25%;
    padding: 10px;
}


/* Episode Section */
#episode .embed,
.embed-responsive-16by9 {
    background-color: transparent;
    border: 0px solid transparent;
}

.watch_mode {
    position: absolute;
    top: 9px;
    left: 91% !important;
    visibility: visible;
    white-space: pre;
    color: white;
}

/* DRY, don't repeat yourself */
.ep_title,
.share,
.ep_title+div,
.watch_mode,
#main,
#episode_navigation {
    margin: 0!important;
}
.ep_title,
.share,
.ep_title+div {
    padding: 0!important;
    height: 0!important;
    visibility: hidden!important;
}

/* Ep Nav */
#episode_navigation {
    border-top: 0;
}
#episode_navigation .row div:nth-child(1),
#episode_navigation .row div:nth-child(2),
#episode_navigation .row div:nth-child(3) {
    width: 33.33%;
}

/* Ep Mirrors*/
.mirrors_info,
#episode_mirrors {
    margin: 5px;
    font-weight: normal;
}
.episode_mirrors_wraper .glyphicon {
    top: 30px;
    left: 393px;
}
.episode_mirrors_name {
    padding-bottom: 20px !important;
}
}
