css:
@-moz-document regexp("https://.*.9anime.*/watch/.*") {
.container {
    max-width: 100%!important
}
#main .content > div:nth-child(1) {
    height: 850px!important
}
.widget.player {
    position: absolute;
    top: 0;
    width: 100%
}
#player {
    height: 760px!important;
    max-height: 760px!important
}
#sidebar {
    margin-top: 850px!important
}
.widget.comment {
    height: 50px!important
}
.vjs-big-play-centered .vjs-control-bar {
    background-color: rgba(0, 0, 0, 0)
}
#jw {
    display: block!important
}
#jwa,
.abmsg,
.error {
    display: none!important
}
}

@-moz-document regexp("https://.*.9anime.*") {
#header {
    padding-top: 10px;
    height: 60px;
    background: #16151c
}
.ads,
.alert.alert-primary.notice,
.col-lg-7.col-sm-24.sidebar,
.col-md-7,
.col-xs-24,
.home-socials,
.item.mbtn.toggler.resize,
.mgbox,
.notification,
.sticky.sticky-left,
.sticky.sticky-right {
    display: none!important
}
.col-lg-17,
.col-md-17 {
    width: 100%
}
.watchpage #player {
    height: 642px!important
}
}
