@-moz-document domain("letterboxd.com") {
body {
    font-size: 1rem;
    color: #CCC;
    background-color: #0A0A0A;
    background-image: linear-gradient(180deg,#0A0A0A 0,#0A0A0A 100px,#0A0A0A 101px);
    background-position: 0 0;
}

body {
    line-height: 1;
    color: #CCC;
    background: #0a0a0a;
}

.site-body {
    background: #0a0a0a;
    padding: 30px 0;
}

