.backdropped .site-header .site-header-bg {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 95px;
    opacity: .7;
    background-color: transparent;
    background-repeat: repeat-x;
    background-image: linear-gradient(180deg,#0a0a0a 0,rgba(10,10,10,.94460641) 16.56%,rgba(10,10,10,.80174927) 30.85%,rgba(10,10,10,.60641399) 43.77%,rgba(10,10,10,.39358601) 56.23%,rgba(10,10,10,.19825073) 69.15%,rgba(10,10,10,.05539359) 83.44%,rgba(10,10,10,0));
}

.actions-panel>ul>li {
    color: #CCC;
    background-color: #0F0F0F;
    text-align: center;
    margin-bottom: 1px;
    padding: .76923077rem 0;
    box-sizing: border-box;
}

.section-heading {
    font-family: Graphik-Regular-Web,sans-serif;
    font-weight: 400;
    font-size: 1rem;
    color: #CCC;
    text-transform: uppercase;
    letter-spacing: .075em;
    margin-top: 0;
    margin-bottom: .76923077rem;
    padding-bottom: 5px;
    border-bottom: 0px solid #008860;
}

.text-slug {
    color: #888;
    background-color: #0f0f0f;
    display: inline-block;
    padding: 3px 6px;
    white-space: nowrap;
    border-radius: 3px;
    box-shadow: none;
}

a.text-slug:active, a.text-slug:hover {
    color: #CCC;
    background: #0f0f0f;
}

.rating-histogram i {
    background: #E0245E;
    border-top-right-radius: 2px;
    border-top-left-radius: 2px;
}

.micro-button {
    color: #F80;
    padding: 4px 5px 3px;
    border: none;
    font-size: .76923077rem;
    text-transform: uppercase;
    letter-spacing: .075em;
    border-radius: 2px;
}

.watch-panel {
    border: none;
    background-color: #0f0f0f;
    overflow: hidden;
    border-radius: 3px;
}

.watch-panel>.title {
    background: #0f0f0f;
    color: #E0245E;
    margin: 0;
    padding: 9px 12px;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: .075em;
}

.watch-panel p~div .services {
    margin: 0 10px;
    border-top: none;
}

.watch-panel .more {
    display: block;
    padding: 6px 10px 4px;
    text-transform: uppercase;
    letter-spacing: .075em;
    font-size: .76923077rem;
    line-height: 1.3;
    border-top: none;
    color: #678;
}

.film-popular-review .film-detail {
    border-color: #151515;
    padding-bottom: 15px;
}

.film .list-set .list {
    border-bottom: 3px solid #101010;
    padding: 15px 0;
    margin: 0;
}

.page-footer {
    background-color: #0a0a0a;
    padding: 32px 0;
}

.backdropped .site-header .search-form .field {
    background-color: #101010;
    box-shadow: none;
    transition: background-color .4s cubic-bezier(.165,.84,.44,1);
}

.backdropped .site-header .search-form .field:focus {
    background-color: #101010;
}

.backdropped .site-header:hover .search-form .field:focus {
    background-color: #101010;
}

.main-nav>.navitems>.nav-account .subnav li a:hover {
    background: #101010;
    color: #CCC;
    text-shadow: none;
}

.main-nav>.navitems>.nav-account .subnav {
    display: none;
    background: #101010;
    position: absolute;
    z-index: 10000;
    box-shadow: none;
    border-radius: 2px;
    padding-bottom: 5px;
    min-width: 100px;
    top: -3px;
    left: -10px;
}

.main-nav>.navitems>.nav-account .subnav .divider {
    border-bottom: none;
    border-top: none;
    margin-top: 5px;
    padding-top: 5px;
}

.main-nav>.navitems>.nav-account .subnav li a {
    display: block;
    color: #CCC;
    border: none;
    padding: 4px 15px;
    text-shadow: none;
}

.no-mobile .review-tiles>.review-tile {
    float: left;
    width: 45.91836735%;
    margin-left: 15px;
    margin-right: 15px;
    margin-bottom: 2.30769231rem;
    padding-bottom: 1.53846154rem;
    border-bottom: none;
}

.no-mobile .content-teaser.-split {
    background: #0f0f0f;
    display: flex;
}

.list-frame, .poster .frame {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 100%;
    white-space: nowrap;
    text-indent: 110%;
    overflow: hidden;
    -webkit-transition: -webkit-box-shadow .1s ease-in;
    box-sizing: border-box;
    box-shadow: none;
    border-radius: 4px;
}

.film-list-wrapper .overlay, .poster .overlay {
    display: block;
    position: absolute;
    top: -3px;
    left: -3px;
    right: -3px;
    bottom: -3px;
    text-indent: 110%;
    overflow: hidden;
    white-space: nowrap;
    transition: border .1s linear;
    border: none;
    border-radius: 3px;
}

.film-list-wrapper .list-frame:hover .overlay,.poster .frame:hover .overlay {
    display: block;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: none;
}

.poster.-attributed .attribution-block {
    display: block;
    box-sizing: border-box;
    position: absolute;
    bottom: 0;
    clear: both;
    width: 100%;
    margin: 0;
    padding: 5px;
    background: #0f0f0f;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
    white-space: nowrap;
}

.poster.-attributed .attribution-block:after {
    content: "";
    position: absolute;
    right: 0;
    top: 50%;
    width: 8px;
    height: 18px;
    margin-top: -9px;
    background: #0f0f0f;
}

.bar-nav {
    color: #CCC;
    background: #0f0f0f;
    display: inline-block;
    padding: 0 2px;
    border: none;
    white-space: nowrap;
    border-radius: 4px;
}

.bar-nav li {
    float: left;
    border-right: 1px solid #151515;
    font-size: .92307692rem;
    line-height: 1;
    text-transform: uppercase;
    letter-spacing: .075em;
}

.smenu-menu {
    float: left;
    padding-bottom: 2px;
    background: #101010;
    position: absolute;
    z-index: 15;
    box-shadow: none;
    border-radius: 3px;
}

.smenu-menu li.smenu-selected {
    color: #CCC;
    display: block;
    text-shadow: none;
    padding-left: 8px;
    border-bottom: none;
    margin-bottom: 4px;
}

.smenu-menu li a.item:hover {
    background: #101010;
    color: #CCC;
    text-shadow: none;
}

.smenu-menu li .smenu-sublabel, .smenu-menu li a.item {
    display: block;
    padding: 4px 8px;
    color: #CCC;
    border: none;
    text-shadow: none;
    font-size: .92307692rem;
}

.field, .textarea, .tt-hint {
    padding: 9px 9px 8px;
    margin: 0;
    border: none;
    background-color: #101010;
    color: #CCC;
    box-shadow: none;
    font-size: 1.07692308rem;
    line-height: 1;
    box-sizing: border-box;
    border-radius: 3px;
}

.field:focus, .textarea:focus, .tt-hint:focus {
    background-color: #101010;
    color: #CCC;
    box-shadow: none;
}

.film-list .film-detail {
    border: none;
}

.people-shortlist>li {
    overflow: hidden;
    border-bottom: none;
    padding: 10px 0;
    color: #CCC;
    position: relative;
}

.button {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    display: inline-block;
    padding: 9px 12px 8px;
    text-decoration: none;
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: .075em;
    font-family: Graphik-Semibold-Web,sans-serif;
    font-weight: 400;
    line-height: 12px;
    border: none;
    box-shadow: none;
    border-radius: 3px;
    color: #CCC;
    background: #101010;
    cursor: pointer;
    margin: 0;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
}

#content-nav {
    position: relative;
    border-bottom: 1px solid #151515;
    font-size: 11px;
    line-height: 1;
    color: #CCC;
    min-height: 30px;
}

.field:disabled, .field:disabled:focus, .textarea:disabled, .textarea:disabled:focus {
    background-color: #101010;
    color: #CCC;
    box-shadow: none;
}

#favourite-films .-placeholder, #favourite-films .placeholder {
    border: none;
    background-color: #101010;
    cursor: pointer;
}

.avatar:after {
    content: "";
    border: none;
    transition: all .2s;
}


.select {
    -webkit-appearance: none;
    border: none;
    padding: 8px 20px 8px 9px;
    margin: 0;
    width: 100%;
    box-sizing: border-box;
    border-radius: 3px;
    cursor: pointer;
    background-color: #101010;
    color: #CCC;
    box-shadow: none;
    background-repeat: no-repeat;
    background-position: 100% 50%;
    
}

.select:hover {
    background-color: #101010;
    box-shadow: none;
    
}

.option-label.-toggle .track {
    display: inline-block;
    background: #151515;
    width: 3rem;
    height: 1.8rem;
    margin-right: 1rem;
    border-radius: 1rem;
    position: relative;
    vertical-align: middle;
}
}
