css:
@-moz-document domain(twitter.com)
{

/* Image credits: Alopex Fansubs */

  *
  {
    border-radius: 0 !important;
  }

  .front-image
  {
    display: none !important;
  }

  .topbar > div.global-nav > div.global-nav-inner
  {
    background: rgba(255,255,255,0.8) !important;
  }

  .PageDownload-info
  {
    background: rgba(255,255,255,0.7) !important;
    padding: 10px !important;
    margin-top: 210px !important;
    border-radius: 3px !important;
  }

  .stream-item
  {
    background: rgba(255,255,255,0.9) !important;
  }

  .dashboard.dashboard-left > div.Trends.module.trends
  {
    background: rgba(255,255,255,0.9) !important;
  }

  .timeline-tweet-box > div.home-tweet-box.tweet-box.component.tweet-user
  {
    background: rgba(255,255,255,0.5) !important;
  }

  #tweet-box-home-timeline.tweet-box.rich-editor.is-showPlaceholder
  {
    background: rgba(255,255,255,0.5) !important;
    color: #333 !important;
  }

  .tweet-content > div.RichEditor
  {
    background: rgba(255,255,255,0.5) !important;
    color: #333 !important;
  }

  .dashboard-right
  {
    display: none !important;
  }

  body
  {
    background: #090909 !important;
  }

  [dir="ltr"].logged-in
  {
    background-position: center !important;
    background-image: url(https://pbs.twimg.com/media/D15kB6BVAAAqRGb.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .logged-out
  {
    background-position: center !important;
    background-image: url(https://pbs.twimg.com/media/D15kB6BVAAAqRGb.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .ProfilePage .AppContent
  {
    background-position: center !important;
    background-image: url(https://pbs.twimg.com/media/D15kB6BVAAAqRGb.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .front-page
  {
    background-position: center !important;
    background-image: url(https://pbs.twimg.com/media/D15kB6BVAAAqRGb.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #page-outer > div#page-container.AppContent.wrapper.wrapper-home > div.dashboard.dashboard-left > div.Trends.module.trends
  {
    background: rgba(0,0,0,0.4) !important;
  }

  #page-outer
  {
    background: transparent !important;
  }

  #doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-bg
  {
    background: transparent !important;
  }

  #front-container.front-container.front-container-full-signup > div.front-card > div.front-signup.js-front-signup
  {
    background: rgba(255,255,255,0.6) !important;
  }

  #doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-signin.js-front-signin
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #doc > div.topbar.js-topbar > div.global-nav.global-nav--newLoggedOut > div.global-nav-inner
  {
    background: rgba(0,0,0,0.7) !important;
  }

  #doc.route-home > div#page-outer > div#page-container.AppContent.wrapper.wrapper-home > div.dashboard.dashboard-left > div.DashboardProfileCard.module
  {
    background: rgba(0,0,0,0.2) !important;
  }

  #doc.route-home > div#page-outer > div#page-container.AppContent.wrapper.wrapper-home > div.dashboard.dashboard-left > div.DashboardProfileCard.module > div.DashboardProfileCard-content > div.DashboardProfileCard-userFields.account-group > div.DashboardProfileCard-name.u-textTruncate > a.u-textInheritColor
  {
    color: #fff !important;
  }
}
