css:
@-moz-document url-prefix("https://exmo.me/wip/") {
/*** Version 25-March-2019 ***/

/* uncomment next line after installing a style */
/* @import url('https://fonts.googleapis.com/css?family=Exo+2:300,400,500,600,700&subset=cyrillic'); */

/* Font - apply Exo 2 font */
html, body, button, input, optgroup, select, textarea {
    font-family: "Exo 2", sans-serif !important;
}
/* Bold font - more boldness */
.text_bold {
    font-weight: 700 !important;
}

/* Main container - move left to allow wider chat on the right */
main {
    margin: 0 0 auto !important;
}

/* Chat - move right */
div[class^="chatindex___"] {
/*    right: 45% !important;*/
    left: calc(100% - 118px)!important;
}

/* Chat - make wider */
div[class^="windowindex___"] {
    width: 400px !important;
}
}
