@-moz-document domain("patreon.com") {
/*-----------------------------------------*/
/* Custom dark mode for Patreon by Yughues */
/*-----------------------------------------*/

/*---------------------------------------------------------------------------------------------------------------*/
/*---------------------------------------- definition of the main colors ----------------------------------------*/
/*---------------------------------------------------------------------------------------------------------------*/

:root {
	/* deepest dark for page background */
	--darkest: hsl(0, 0%, 10%);

	/*  */
	--darker: hsl(0, 0%, 20%);

	/*  */
	--dark: hsl(0, 0%, 40%);

	/*  */
	--light: hsl(0, 0%, 60%);

	/*  */
	--lighter: hsl(0, 0%, 80%);

	/*  */
	--lightest: hsl(0, 0%, 90%);

	/* main color for important elements like backgrounds, links and titles */
	/*--PatreonOrange: rgb(249, 104, 84); --- Original */
	--PatreonOrange: hsl(20, 80%, 50%);

	/* main complementary color for text and sidebar */
	/*--PatreonBlue: rgb(5, 45, 73); --- Original */
	--PatreonBlue: hsl(195, 75%, 35%);
}

/*--------------------------------------------------------------------------------------------------------*/
/*---------------------------------------- default link behaviour ----------------------------------------*/
/*--------------------------------------------------------------------------------------------------------*/

a,
a:link
{
	color: var(--PatreonOrange) !important;
}

a:hover,
a:active
{
	color: var(--lightest) !important;
}

a:visited:not(:hover)
{
	color: var(--PatreonBlue) !important;
}

/*-----------------------------------------------------------------------------------------*/
/*---------------------------------------- BUTTONS ----------------------------------------*/
/*-----------------------------------------------------------------------------------------*/

/* orange button */
[role="button"],
[role="button"][color="primary"],
[role="button"][color="secondary"],
[type="submit"]
{
	background-color: var(--PatreonOrange) !important;
}


[role="button"] div,
[role="button"][color="primary"] div,
[role="button"][color="secondary"] div,
[type="submit"] div
{
	color: var(--lightest) !important;
}

_
{
	fill: #fff !important;
}


[role="button"]:hover,
[role="button"][color="primary"]:hover,
[role="button"][color="secondary"]:hover,
[type="submit"]:hover
{
	background-color: var(--lightest) !important;
}


[role="button"]:hover div,
[role="button"][color="primary"]:hover div,
[role="button"][color="secondary"]:hover div,
[type="submit"]:hover div
{
	color: var(--PatreonBlue) !important;
}

_
{
	fill: var(--PatreonBlue) !important;
}

/* clear button */
[type="button"][color="tertiary"]
{
	background-color: var(--lightest) !important;
}

[type="button"][color="tertiary"] div
{
	color: var(--PatreonBlue) !important;
}

[type="button"][color="tertiary"]:hover
{
	background-color: var(--PatreonOrange) !important;
}

[type="button"][color="tertiary"]:hover div
{
	color: var(--lightest) !important;
}

[type="button"][color="tertiary"]:hover svg [data-stroke]
{
	stroke: var(--lightest) !important;
}

/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------- BACKGROUNDS ----------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

/* removing background-color where not needed */
.reactWrapper,
.sc-jDwBTQ /* search icon */,
#reactTarget .reactWrapper header form [aria-label="Search"] > div > div:last-child,
button.sc-jAaTju /* avatar */,
.searchShareArea /* Patron manager main blocks */,
[aria-disabled="false"], [aria-disabled="false"]:hover,
.LJBPU /* upload image file */,
.Jg--pages-make_a_post-components-PostEditor-components-AudioMedia--adders > div /* new post audio */
{
	background: transparent !important;
}

/* darkest */
body,
#renderPageContentWrapper > div > div[data-tag="components-alert-contentsDiv"],
.x1n0xj-0 /* content background */
{
	background-color: var(--darkest) !important;
}

/* darker */
#reactTarget > .reactWrapper > div > div > header,
[data-tag="dropdown-list"],
.eNcteU::after /* nav line */,
#renderPageContentWrapper > div > div:nth-child(3) > div,
#reactTarget > .reactWrapper > div > div > div > nav[aria-label="Creator Navigation"],
[data-tag="leftSidebar"] div[overflow] /* left side blocks */,
[data-tag="pitchContainer"] > div > div, /* description */
[data-tag="post-card"] > div > div, /* posts */
.cvacfA /* post poll */,
[aria-label="Site Footer"],
.emptyNotification /* Patron manager right side block */,
.sc-jAaTju div[overflow="hidden"],
.twoBoxesWrapper /* Patron manager main blocks  */,
.te0jwt-1 /* post filters */,
.caAsDT, .fcevlo /* messages */,
.s1rugay5-0 /* supporting */,
._1zF-pages-make_a_post-components-DraftCard--cardWrapper > div /* posts published */,
.diHgjk, .hyFkdV /* new post */,
.ispdkB, .ljzHfA,
.sc-bxivhb.cHSMhf > div > div,
.sc-bxivhb.cHSMhf > div > a > div,
.sc-bxivhb.cHSMhf > div > div > div,
.duAdGN /* tiers */,
.jZvzXh /* tags */
{
	background-color: var(--darker) !important;
}

/* dark */
._3RO-pages-make_a_post-components-PostEditor-components-Media-components-PostFileInput--adderBox,
.s1nuqwgy-0 /* public or patron only */,
.s1k7wtve-1:nth-child(2n) td /* table lines */,
input[data-tag="tags-auto-complete"]

{
	background-color: var(--dark) !important;
}

/* light */
_
{
	background-color: var(--light) !important;
}

/* lighter */
_
{
	background-color: var(--lighter) !important;
}

/* lightest */
.s16fajh2-0:hover /* message tabs */,
.s1ywaurn-0:hover:not(.gXbSGf) /* messages received */
{
	background-color: var(--lightest) !important;
}

/* Orange */
.jMXLLI /* nav underline */,
.qYXwo /* homa nav underline */ ,
.jwCIzE /* nav underline compact mode */,
.sc-VigVT /* notification number alert */,
.s1obcalr-1[color="primary"] /* goal bar completed */,
.hjukWZ /* automatic transfert on/off */,
.bBxoVh /* radio/check input */
{
	background-color: var(--PatreonOrange) !important;
}

/* Blue */
.fgRIGu /* all posts */,
.vn3771-0:hover /* lists li */
{
	background-color: var(--PatreonBlue) !important;
}

/*-----------------------------------------------------------------------------------------*/
/*---------------------------------------- BORDERS ----------------------------------------*/
/*-----------------------------------------------------------------------------------------*/

/* dark */
*
{
	border-color: var(--dark) !important;
}

/* none */
#renderPageContentWrapper [data-tag="components-alert-contentsDiv"]
{
	border: none !important;
}

/* darkest */
.cRULwW:not(:last-child) /* supported by */,
.cKGmsE /* search your creators */
{
	border-color: var(--darkest) !important;
}

/* darker */
.cLiOZx /* showing all creators list */,
.fvhLOV /* home all posts */,
.searchShareArea /* patron manager */,
.sc-bdVaJa,
.emptyNotification /* Patron manager right side block */,
.twoBoxesWrapper /* Patron manager main blocks  */
{
	border-color: var(--darker) !important;
}

/* light */
.gKaqqj /* supporting column */,
.qBgHA /* filter by creators */,
textarea.htQHnU:not(:focus) /* comment textarea */,
.llURae /* patron note */
{
	border-color: var(--light) !important;
}

/* lighter */
_
{
	border-color: var(--lighter) !important;
}

/* Orange */
_
{
	border-color: var(--PatreonOrange) !important;
}

/* Blue */
.fgRIGu /* all posts */,
.llURae:hover /* patron note */
{
	border-color: var(--PatreonBlue) !important;
}

/*---------------------------------------------------------------------------------------*/
/*---------------------------------------- TEXTS ----------------------------------------*/
/*---------------------------------------------------------------------------------------*/

/* darkest */
_
{
	color: var(--darkest) !important;
}

/* darker */
.sc-bZQynM.esolEa h4,
.cYgXYP:nth-child(2n)
{
	color: var(--darker) !important;
}

/* dark */
[color="gray"],
.cRULwW:hover span /* country selection */
{
	color: var(--dark) !important;
}

/* light */
[color="gray2"],
a[data-tag="post-published-at"]:not(:hover),
[aria-label="Site Footer"] a:not(:hover) /* footer logos */,
.iWgZQP[color="dark"] /* guess what - become a patron */,
p.sc-kGXeez.jacZCY /* Tax information */,
#mainmiddlePan h1 /* Patron manager */
{
	color: var(--light) !important;
}

/* lighter */
#component-playground,
.reactWrapper,
.trst5v-0 span /* creator tools sub-nav li active */,
.efUFEK span /* goals text */,
.hbBSis [color="dark"],
.stackable [data-tag="comment-body"] span /* post comments */,
.emptyNotification /* patron manager */
{
	color: var(--lighter) !important;
}

/* lightest */
header[role="banner"] form input,
#renderPageContentWrapper [data-tag="components-alert-contentsDiv"] span,
[direction="column"] .sc-kgoBCf:hover /* top right roll-menu */,
.sc-bZQynM.egvNZS [color="dark"] /* overview nav when compact */,
._3XQ-pages-make_a_post-components-PostEditor--richTextEditor /* post text editor */,
.cmqbxl-0.jHWpqz /* post tags */
{
	color: var(--lightest) !important;
}

/* White */
button[aria-expanded="true"] .sc-htpNat,
.sc-jlyJG:not([disabled]):hover span /* top bar avatar hover menu */
{
    color:#fff !important;
}

/* Orange */
[color="primary"],
[color="dark"],
[color="gray1"],
.gdHSVn,
.trst5v-0.TComK span /* creator tools sub-nav li active */,
.cYgXYP [style="color: rgb(79, 104, 122);"] /* table lines % */,
#mainmiddlePan strong /* patron manager total processed */,
.emptyNotification .showPopup /* patron manager */
{
	color: var(--PatreonOrange) !important;
}

/* Blue */
button[aria-expanded="false"]:hover .sc-htpNat,
header [data-tag="dropdown-list"] li:hover [color="dark"]
{
	color: var(--PatreonBlue) !important;
}

/*-------------------------------------------------------------------------------------*/
/*---------------------------------------- SVG ----------------------------------------*/
/*-------------------------------------------------------------------------------------*/

/* Orange */
header [aria-label="Go to home page"] svg [data-fill] /* Patreon logo */,
.s126e0qj-0 svg g /* like icon */
{
	fill: var(--PatreonOrange) !important;
}

svg [data-stroke]
{
	stroke: var(--PatreonOrange) !important;
}

/* dark */
.hVNxBw svg [data-color], .hVNxBw svg [data-fill]
{
	fill: var(--darkest) !important;
}


/*------------------------------------------------------------------------------------------------*/
/*---------------------------------------- VARIOUS TWEAKS ----------------------------------------*/
/*------------------------------------------------------------------------------------------------*/

.s184zdtz-0.imZQhz /* side naviguation */
{
    top:65px !important;
}

.kRcCcI {
    background-color: transparent !important;
    border: 1px solid var(--PatreonOrange) !important;
}

.kRcCcI:hover, .kRcCcI:focus {
    background-color: #fff !important;
    border: 1px solid #fff !important;
}

._1fo-pages-messages-containers--card a[href^="/messages/"].fSONZM /* messages received */ {
    border-left-color:var(--PatreonBlue) !important;
    border-left-width:4px !important;
}

.s10lum3v-0 /* post text gradient */
{
    background-image:linear-gradient(hsla(0, 0%, 20%, 0) 0%, var(--darker) 100%) !important;
}

.jnFGsM /* messages nav */
{
    box-shadow: var(--PatreonBlue) 0px -3px 0px inset !important;
}

.epVXLq
{
    border-left-color: var(--PatreonBlue) !important;
    border-left-width: 5px !important;
}

.bYBynR /* new post image url */
{
    color: var(--lightest) !important;
    background-color: transparent !important;
}

.bYBynR:focus /* new post image url */
{
    color: var(--darker) !important;
    background-color: var(--lightest) !important;
}
}
