css:
@-moz-document domain("trade.oanda.com") {

button.style-scope.account-summary {
    background-color: #131722;
    color: #ffffff;
}

div.grid.style-scope {
    background-color: #131722;
    color: #ffffff;
}

th.align-left.style-scope {
    background-color: #131722;
    color: #ffffff;
}

th.align-right.style-scope {
    background-color: #131722;
    color: #ffffff;
}

div.tabBar-addButton-container {
    background-color: #131722;
    color: #ffffff;
}

li.p-TabBar-tab {
    background-color: #131722 !important;
    color: #ffffff !important;
}

li.p-TabBar-tab.ordersPortfolioManager.p-mod-closable {
    background-color: #131722;
    color: #ffffff;
}

.transactions-portfolio-manager-0 .grid.transactions-portfolio-manager tr.transactions-portfolio-manager:nth-child(even), 
.trades-portfolio-manager-0 .grid.trades-portfolio-manager tr.trades-portfolio-manager:nth-child(even), 
.positions-portfolio-manager-0 .grid.positions-portfolio-manager tr.positions-portfolio-manager:nth-child(even), 
.orders-portfolio-manager-0 .grid.orders-portfolio-manager tr.orders-portfolio-manager:nth-child(even),
.transactions-portfolio-manager-0 .grid.transactions-portfolio-manager .mobile.transactions-portfolio-manager .row.transactions-portfolio-manager:nth-child(even),
.trades-portfolio-manager-0 .grid.trades-portfolio-manager .mobile.trades-portfolio-manager .row.trades-portfolio-manager:nth-child(even),
.portfolio-portfolio-manager-0 .grid.portfolio-portfolio-manager .mobile.portfolio-portfolio-manager .row.portfolio-portfolio-manager:nth-child(even),
.orders-portfolio-manager-0 .grid.orders-portfolio-manager .mobile.orders-portfolio-manager .row.orders-portfolio-manager:nth-child(even) {
    background-color: #21283c;
}


.p-TabBar-content {
    border-bottom: 1px solid #21283c;
}

.p-TabBar-tab {
    border-right: 1px solid #4d4d4d;
}

.p-TabBar-tab.p-mod-current:not(:first-child) {
    border-left: 1px solid #888888;
}

.rates-component-0 .container.rates-component, .position-ratios-0 *.position-ratios, .news-manager-0 paper-menu.news-manager, .order-book-0 neon-animated-pages.order-book div.order-book, 
.order-book-0 .container-with-overflow.order-book, .order-book-0 .instrument-item.order-book {
    background-color: #131722;
    color: #ffffff;
}

.oanda-search-bar-0 .__search-bar-input.oanda-search-bar, 
.news-manager-0 .header.news-manager,
.news-manager-0 .input.news-manager {
    background-color: #333f46;
    color: #ffffff;
}


.p-DockPanel .p-DockPanel-handle {
    background-color: #404040;
}

.collapsible-selector-0 nav.collapsible-selector {
    color: #ffffff;
}

.panel-view-quote-0 {
    background-color: #131722;
}

.panel-view-quote button {
    color: #ffffff;
}

.toggle-element {
    background-color: #121a21;
}

.p-TabBar .tabBar-icon-container {
    background-color: unset;
    border-right: 1px solid #2e2e2e;
}

.p-DockPanel .p-DockPanel-tabBar {
    background: #131722;
}

.account-summary-0 .margin-percent-container.account-summary {
    background-color: #21283c;
}

.p-TabBar .tabBar-addButton-container {
    border-bottom: 1px solid #21283c;
}

.transactions-portfolio-manager-0 .grid.transactions-portfolio-manager header.transactions-portfolio-manager > div.transactions-portfolio-manager, .transactions-portfolio-manager-0 .grid.transactions-portfolio-manager table.transactions-portfolio-manager thead.transactions-portfolio-manager tr.transactions-portfolio-manager th.transactions-portfolio-manager, .positions-portfolio-manager-0 .grid.positions-portfolio-manager header.positions-portfolio-manager > div.positions-portfolio-manager, .positions-portfolio-manager-0 .grid.positions-portfolio-manager table.positions-portfolio-manager thead.positions-portfolio-manager tr.positions-portfolio-manager th.positions-portfolio-manager, .orders-portfolio-manager-0 .grid.orders-portfolio-manager header.orders-portfolio-manager > div.orders-portfolio-manager, .orders-portfolio-manager-0 .grid.orders-portfolio-manager table.orders-portfolio-manager thead.orders-portfolio-manager tr.orders-portfolio-manager th.orders-portfolio-manager, .trades-portfolio-manager-0 .grid.trades-portfolio-manager header.trades-portfolio-manager > div.trades-portfolio-manager, .trades-portfolio-manager-0 .grid.trades-portfolio-manager table.trades-portfolio-manager thead.trades-portfolio-manager tr.trades-portfolio-manager th.trades-portfolio-manager {
    border-bottom: solid 1px #21283c;
    background-color: #131722 !important;
}

.account-selector-0 .header.account-selector {
    background-color: #21283c;
    color: #ffffff;
}

.rates-panel-selector-0 .view-selector.rates-panel-selector {
    background-color: #202e38;
}

.client-hub-0 .layout-container.client-hub {
    background-color: #131722;
}


.account-selector-0 .main-container.account-selector {
    background-color: #131722;
}

.account-summary-0 button[tabindex="0"].account-summary:hover {
    background-color: #131722;
}

.highcharts-container {
    --oanda-black: #ffffff;
    --oanda-blue: #1670B1;
    --oanda-charcoal: #333F46;
    --oanda-dark-green: #199D22;
    --oanda-ghost-white: #131722;
    --oanda-green: #28C933;
    --oanda-light-blue: #1D98F0;
    --oanda-light-grey: #21283c;
    --oanda-orange: #FC9728;
    --oanda-red: #E53935;
    --oanda-slate-grey: #697683;
    --oanda-white: #000000;
    --oanda-yellow: #FFE94E;
    --oanda-price-decrease: var(--oanda-red);
    --oanda-price-increase: var(--oanda-green);
    --oanda-font-main: 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
    font-family: var(--oanda-font-main) !important;
    box-sizing: border-box;
}

.list-view-quote-0 {
    background-color: #131722
}


.list-view-quote-0 .symbol-text.list-view-quote {
    color: #ffffff;
}



.list-view-quote-0 .symbol-text.list-view-quote {
    color: #ffffff;
}

.news-manager-0 .unread.news-manager {
    font-weight: bold;
    background-color: #131722;
    color: #ffffff;
}

.news-manager-0 .read.news-manager {
    font-weight: normal;
    background-color: #131722;
    color: #8f8f92;
}

.news-manager-0 .unread.news-manager:hover, .news-manager-0 .unread.news-manager:focus {
    background-color: #363636;
}

}
