css:
@-moz-document domain("dotaautochess.gamepedia.com") {
body,
.mw-body,
table.wikitable,
#mw-content-text > div > table > tbody > * > th,
#footer,
#footer ul li {
    background-color: #333;
    color: #fff;
}

table.wikitable > * > tr > th{
    color: #000;
    background-color: #fec500;
}


h1, h2, h3, h4, h5, h6,
.tocnumber {
    color: #fec500;
}

div.vectorTabs li.selected {
    background-color: #555;
}

a,
.mw-parser-output a.external {
    color: #74beff;
}

a:visited {
    color: #299bff;
}

table.wikitable > * > tr > td,
table.wikitable > * > tr > th {
    border: 1px solid #fff;
}
}
