css:
@-moz-document domain("wwnorton.com") {
/*Pending goals:
    - Make items in OUTLINE underlined to clearly indicate linking
    - Solve definition boxes (revealed on click) being too small & the size of their containers being unrelated to the content they contain
*/


/*Styles which change main display-----------------------------------------*/

/*Sets main color scheme*/
body, .row div p samp, .List-6 ins {
    color: white;
    background-color: black;
}

/*Excerpt*/
.List-6 {
    border: 3px solid white;
}


/*Sets HEADER color and size*/
p.H1 ins, p.CT ins {
    color: #ff5a2d;
    font-size: 70px;
}

/*Sets size of every word*/
ins {
    font-size: 35px;
    line-height: 1;
}

/*Highlights keywords*/
.GL-B {
    color: orange;
}

p.H2 ins {
    color: hsla(188, 100%, 50%, 1);
}

/*DOES NOTHING CURRENTLY Highlights bullet-points*/
/*  {
    color: hsl(132, 100%, 50%);
} */

/*Highlights bullet points in the FACT BOX*/
ul {
  list-style: none;
}
div.FACT-Box-sp ul li ul li::before {
    content: "\2022";
    color: red;
    font-weight: bold;
    font-size: 50px;
    display: inline-block;
    margin-left: -20px;
    margin-top: 15px;
    position: absolute;
}






/*Highlights source links*/
span.fn-ref {
    color: #fff100;
}

/*Fixes text color of system highlighting when searching for text*/
.highlighted_search_term {
    color: black;
}

/*Puzzle boxes*/
.ch03-box div p samp {
    background-color: #f2f1e6;
    color: black;
    line-height: 1;
}


/*Fixes formatting for FACT-box: first header, then body*/
.FACT-Box table tbody tr td p samp ins {
    color: black;
    background-color: #f2f1e6;
}
.FACT-Box {
    background-color: white;
}
.FACT-Box .FACT-Box-sp p samp ins {
    background-color: white;
    color: black;
}


/*Other displayed items------------------*/

/*Reveals extra feature menu contents*/
.ncia_popup_menu {
    color: green;
}

/*Comment boxes around photos have black background and border*/
div.PH-CAP-co {
    background-color: black;
    border-bottom: 2px white solid;
    border-right: 2px white solid;
    border-left: 2px white solid;   
}
    /*Applies to text beneath images*/
    div.PH-CAP-co p samp ins {
        line-height: 1; 
    }
    
/*Makes bottom and top panels visible against white background*/
#bottom_panel,
#top_panel {
    color: black;
    background-color: white;
}

/*Fixes things outside of main reading app*/
.shopping-cart {
    color: black;
}

/*Sets boxes with important info apart from other inline text.*/
/*header text component*/
p.H3 samp ins {
    background-color: white;
    color: hsl(188, 100%, 35%);
    
}
    /*Main body text component*/
    p.H3{
        background-color: white;
        padding: 6px;
        margin: 10px 0;
    }
    /*Targets indented, italicized text*/
    p.EXT-F, p.EXT-F ins {
    background-color: white;
    color: black;
    padding: 0;
    margin: 10px 0;
    line-height: 1.3;
}

/*Removes styling for first character in paragraph*/
.Sans-Roboto-Bold-SC-Red1{
    margin: 0;
    line-height: 0;
    float: none;
    color: white;
    font-family: "Roboto-Light", sans-serif;
}
    /*Handles instances of same problem as above except in Puzzle Question boxes*/
    .Dropcap1 {
        margin: 0;
        line-height: 0;
        float: none;
        color: black;
        font-family: 'Roboto-Regular', sans-serif

    }

/*"Think About It" divider and text coloring*/
div.BXQH-Box {
    border-top: 6px solid #DA471F;
}
p.BXQH {
    color: #DA471F;
}

/*Further reading and keyterms header*/
.EOH1, li.EOFR {
    background-color: white;
    padding: 5px;
}
/*Keyterms table*/
p.EOKT {
    background-color: white;
    padding: 6px 31px;
    margin: 10px;
}


}
