css:
@-moz-document url-prefix("https://www.tigotrainers.com/") {
input, textarea, table, input[type=submit], buttom, .buttom, .small-buttom, input, select, textarea, .input, .content, .chosen-container, .chart, #sub_filter a, #day, #calendar, #calendar .week .day .event b, #calendar .week .day .event {
    color: #ffffff;
    background-color: rgba(0, 0, 0, .5);
}
.chart, #sub_filter a, #day, #calendar, #calendar .week .day .event b, #calendar .week .day .event {
    background-color: rgba(0, 0, 0, .5) !important;
}
html, body, .background, #popup #content {
    background-color: #00377b;
    color: #ffffff;
}
.background, .form-wrap.form-builder .frmb .form-elements {
    background-color: #0b284c;
}
body *, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, p, a {
    color: #ffffff;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, a, #calendar .week .day .event b, #calendar .week .day .event i {
    color: #00c6f4 !important;
}
table tr {
    background-color: rgba(0, 0, 0, 0);
}
table tr:nth-child(2n) {
    background-color: rgba(0, 0, 0, .2);
}
table thead tr {
    background-color: #00c6f4 !important;
    border-color: #00377b;
}
table tr td, table tr th {
    border-right: 1px dashed rgba(0, 0, 0, .2);
}
table tr:hover, #calendar .week .day:hover, #day .hours .hour:hover {
    background-color: #0b284c !important;
}
.blue {
    color: #00c6f4 !important;
}
.black {
    color: #ffffff !important;
}
th .light-blue {
    color: #00377b !important;
}
.chosen-container .chosen-single, .chosen-container .chosen-single span, .chosen-container .chosen-single div, .buttom {
    color: #ffffff !important;
}
#day .hours .hour {
    color: #00377b;
}
.column-segment {
    background: rgba(0, 0, 0, 0) !important;
}
.form-wrap.form-builder .frmb li, .form-wrap.form-builder .form-control, .form-wrap.form-builder .frmb .form-elements input[type=color], .form-wrap.form-builder .frmb .form-elements input[type=date], .form-wrap.form-builder .frmb .form-elements input[type=number], .form-wrap.form-builder .frmb .form-elements input[type=text], .form-wrap.form-builder .frmb .form-elements select, .form-wrap.form-builder .frmb .form-elements textarea, .form-wrap.form-builder .frmb .prev-holder input[type=number], .form-wrap.form-builder .frmb .prev-holder input[type=text], .form-wrap.form-builder .frmb .prev-holder select, .form-wrap.form-builder .frmb .prev-holder textarea {
    color: #ffffff;
    background-color: rgba(0, 0, 0, .5);
}
input:hover, input:focus, select:hover, select:focus, .form-wrap.form-builder .form-control:focus, .form-wrap.form-builder .frmb .form-elements input[type=color]:focus, .form-wrap.form-builder .frmb .form-elements input[type=date]:focus, .form-wrap.form-builder .frmb .form-elements input[type=number]:focus, .form-wrap.form-builder .frmb .form-elements input[type=text]:focus, .form-wrap.form-builder .frmb .form-elements select:focus, .form-wrap.form-builder .frmb .form-elements textarea:focus, .form-wrap.form-builder .frmb .prev-holder input[type=number]:focus, .form-wrap.form-builder .frmb .prev-holder input[type=text]:focus, .form-wrap.form-builder .frmb .prev-holder select:focus, .form-wrap.form-builder .frmb .prev-holder textarea:focus, .form-wrap.form-builder .form-control:hover, .form-wrap.form-builder .frmb .form-elements input[type=color]:hover, .form-wrap.form-builder .frmb .form-elements input[type=date]:hover, .form-wrap.form-builder .frmb .form-elements input[type=number]:hover, .form-wrap.form-builder .frmb .form-elements input[type=text]:hover, .form-wrap.form-builder .frmb .form-elements select:hover, .form-wrap.form-builder .frmb .form-elements textarea:hover, .form-wrap.form-builder .frmb .prev-holder input[type=number]:hover, .form-wrap.form-builder .frmb .prev-holder input[type=text]:hover, .form-wrap.form-builder .frmb .prev-holder select:hover, .form-wrap.form-builder .frmb .prev-holder textarea:hover, .form-wrap.form-builder .empty .frmb {
    background-color: rgba(0, 0, 0, .7);
}
}
