css:
@-moz-document url-prefix("https://lms.lafilm.edu/") {
p,
h1,
h2,
span,
.card__meta-label,
.card__text-primary,
.sub.header,
.ui.dropdown,
.menu>.message.normal,
.account-circle__location,
li,
ul{
    color: black !important
}
.circle-progress__bar{
    stroke: #ffeda6 !important;
}
h5.ui.header,
.card__text-secondary,
.header__subtitle,
.global-alert__text,
.progress-bar__text-label,
.progress-bar__text-secondary,
.circle-progress.circle-progress--completed,
time,
.date,
.circle__content{
    color: #444 !important
}
body,
.panel-links__link.active,
.ui.fluid.button,
.menu .selected.item,
.fluid-container-layout,
.exam-detail__footer{
    background-color: #666 !important;
}
.fa-chevron-down,
.fa-chevron-up {
    color: white !important;
}
.toolbar,
.progress-bar__closed-container,
.cards__group,
.progress-bar__container,
.notification-panel,
.message-panel,
.panel-left,
.panel-scrollable-content,
.show.sticky-header,
.redactor__attachments,
.redactor-toolbar,
.navigation-footer,
.topbar,
.fluid-container-layout__topbar,
.lms-loading,
.login{
    background-color: gray !important;
}
.circle-progress__rail {
    stroke: #444 !important;
}
.menu {
    background: gray !important;
}
a,
.fa-ellipsis-h,
.progress-bar__text-calculator,
.help-text,
.exam-detail__progress,
.tabs__control{
    color: #ffeda6 !important;
}
.global-alert--high,
.circle-progress.circle-progress--completed,
.circle.circle--filled,
.ui.progress .bar{
    background-color: #ffeda6 !important;
}
.tabs__item--active, .tabs__item--active:hover{
    border-bottom-color: #ffeda6 !important;
}
.circle-progress {
    background-color: #fff0 !important
}
.toolbar {
    min-height: 0 !important;
    padding-left: 1.25rem;
}
.circle-progress__badge,
.notice-indicator,
.topbar,
.progress-bar__closed-container,
.toolbar,
.tabs,
.panel-right,
.show.sticky-header,
.circles-header__circle,
.circle.circle--filled{
    border: none !important;
}

/* make it bigger*/
.login__logo-svg>img{
    height: 300% !important;
    width: 300% !important;
}

.login__logo-svg{
    margin-left: 0px !important;
}
}
