css:
@media only screen and (min-aspect-ratio: 17/9)
{
    .nf-player-container .video-container video, .VideoContainer video, .nfp video/* NETFLIX */
    {
        height: 135%!important;
        width:135%!important;
        min-width:100%;
    }

    nfp, nf-player-container, container-large
    {
        width:135%!important;
        min-width:100%;
    }
    .VideoContainer video {
        left: -17.5%!important;
    }
    .ytp-fullscreen video.video-stream.html5-main-video /* YOUTUBE */
    {
        left: 0px!important;
        top: 0px!important;
        margin-top: -156px!important; /* cropping top black bar */
        margin-bottom: -156px!important; /* cropping bottom black bar */
        zoom: 1.335!important; /* zooming to fit */
        width:auto!important;
    }
}
