css:
@-moz-document url-prefix("https://www.shogakukan.co.jp/pr/tencomi/doraemon/") {
#mainMenu {
    width: 100%;
    background: #17171c;
    padding: 1% 0;
    top: 0;
    z-index: 2;
}
#loading {
    width: 140%;
    height: 140%;
    background: #000;
    position: fixed;
    z-index: 10;
    top: -20%;
    left: -20%;
}
header{
margin:0;
background:#000;
padding-top:5%;
padding-bottom:5%;
}
footer {
    background: #000;
    width: 100%;
    padding: 6% 0 2% 0;
}
footer p, footer p span {
    color: #fff;
    line-height: 1.4em;
}
* {
    margin: 0;
    padding: 0;
    outline: none;
    border: 0;
    line-height: 1.5em;
    font-size: 0;
    text-align: center;
    background-color: #000;
}
#fixMenu {

    position: fixed;
    top: -200px;
    left: 0;
    background: #17171c;
    padding: 3px 0;
    z-index: 2;
}
#lineup1 {

    background: url(https://none.none) #000 center top;
        background-size: auto;
}
