css:
@-moz-document domain("discordapp.com")
{



/* Hiding */
.downloadAppButton-3FZHkU /* Download banner. */
{
 display: none !important;
}

/* Resizing and Spacing */

.scroller-2FKFPG .wrapper-sa6paO,
.scroller-2FKFPG .wrapper-25eVIn,
.wrapper-25eVIn svg
{
 height: 30px;
 line-height: 30px;
 background-size: cover;
}

.scrollbar-2rkZSL /* 1px too narrow */
{
 width: 17px;
}

/* Overflow until hover */
.flex-1xMQg5 > .channels-Ie2l6A
{
 width: 20px;
 border-right: 3px solid #222;
 transition: all 0.15s;
}
.membersWrap-2h-GB4,
.membersWrap-2h-GB4 .scrollbar-2rkZSL
{
 background-image: linear-gradient( to top, #2a2c31 51px, #2f3136 0);
}
.membersWrap-2h-GB4:not(:hover),
.membersWrap-2h-GB4:not(:hover) .members-1998pB
{
 min-width: 20px;
 width: 70px;
 max-width: 70px;
}
.memberOffline-2lN7gt:not(:hover)
{
 opacity: 0.2;
}
.membersWrap-2h-GB4:not(:hover) .scroller-2FKFPG div[class*="membersGroup"]
{
 display: none;
}
.membersWrap-2h-GB4:not(:hover) .members-1998pB
{
 padding-right: 0;
}
.flex-1xMQg5 > .channels-Ie2l6A:hover,
.membersWrap-2h-GB4:hover
{
 width: 240px;
 border-right: 0;
}
.flex-1xMQg5 > .channels-Ie2l6A:not(:hover) .header-2o-2hj
{
 padding-left: 0;
 padding-right: 0px;
}
.flex-1xMQg5 > .channels-Ie2l6A:not(:hover) .header-2o-2hj > svg
{
 transform: rotateZ(-90deg);
}
.flex-1xMQg5 > .channels-Ie2l6A:not(:hover) .container-2Thooq
{
 height: 49px;
}
.flex-1xMQg5 > .channels-Ie2l6A:not(:hover) .wrapper-2F3Zv8,
.flex-1xMQg5 > .channels-Ie2l6A:not(:hover) .flex-1xMQg5
{
 display: none;
}
.contentCompact-1QLHBj
{
 position: relative !important;
}
.latin24CompactTimeStamp-3m4PhB
{
 position: relative !important;
 width: calc(25px + 8ch);
 margin-left: 25px;
}
.buttonContainer-KtQ8wc
{
 position: absolute !important;
 top: 2px !important;
 left: 0 !important;
 z-index: 10 !important;
}
.button-3Jq0g9
{
 order: 1;
 padding-left: 10px;
 margin-right: 0;
}
.reactionBtn-2na4rd
{
 order: 2;
 margin-left: 0;
 margin-right: 0;
}
.isCompact-1hsne1
{
 text-indent: -10ch;
}

/* Rearranging the order of the users */
.content-yTz4x3 > .membersWrap-2h-GB4
{
 order: 1 !important;
}
.content-yTz4x3 > .spacer-1fA9zc
{
 order: 2 !important;
}
/* Member popout */   /* Immediate child */
.popouts-2bnG9Z > div.popoutLeft-3aViER,
/* ------------------------ Search popout  and also search popout */
.popouts-2bnG9Z > div:not(.popout-2iWAc-):not(.noShadow-3pu20z)
{
 left: 100px !important;
 transform: translateX(50px) translateY(-40px) !important;
}

/* Colors */
.theme-dark .members-1998pB,
.scrollbar-2rkZSL > .pad-29zQak
{
 background-color: transparent !important;
}
.theme-dark .friendsOnline-2JkivW
{
 background-color: #0c9;
 color: #000;
}


}
