@-moz-document domain("youtube.com") {
html:not(.style-scope)[dark], :not(.style-scope)[dark] {
    --yt-spec-brand-background-solid: #282828;
    --yt-spec-brand-background-primary: rgba(40, 40, 40, 0.98);
    --yt-spec-brand-background-secondary: rgba(40, 40, 40, 0.95);
    --yt-spec-feed-background-a: #1F1F1F;
    --yt-spec-feed-background-b: #141414;
    --yt-spec-feed-background-c: #0A0A0A;
    --yt-spec-error-background: #F9F9F9;
    --yt-spec-text-primary: #FFFFFF;
    --yt-spec-text-primary-inverse: #0A0A0A;
    --yt-spec-text-secondary: #AAAAAA;
    --yt-spec-text-disabled: #717171;
    --yt-spec-call-to-action: #003f75;
    --yt-spec-icon-active-other: #969696;
    --yt-spec-icon-inactive: #909090;
    --yt-spec-icon-disabled: #606060;
    --yt-spec-badge-chip-background: rgba(255, 255, 255, 0.10);
    --yt-spec-suggested-action: #252A3A;
    --yt-spec-button-chip-background-hover: rgba(255, 255, 255, 0.20);
    --yt-spec-touch-response: #FFFFFF;
    --yt-spec-filled-button-text: #0A0A0A;
    --yt-spec-call-to-action-inverse: #065FD4;
    --yt-spec-brand-icon-inactive: #909090;
    --yt-spec-brand-button-background: #CC0000;
    --yt-spec-brand-link-text: #FF4E45;
    --yt-spec-brand-subscribe-button-background: #FF0000;
    --yt-spec-wordmark-text: #FFFFFF;
    --yt-spec-10-percent-layer: rgba(255, 255, 255, 0.10);
    --yt-spec-themed-blue: #3EA6FF;
    --yt-spec-themed-green: #2BA640;
}
html:not(.style-scope)[dark], :not(.style-scope)[dark] {
--yt-std-body-300: hsla(0, 100%, 100%, .5);
    --yt-std-surface-200: hsl(0, 0%, 11%);
    --yt-std-surface-300: hsl(0, 0%, 14%);
    --yt-std-surface-400: hsl(0, 0%, 16%);
    --yt-primary-color: hsla(0, 100%, 100%, .88);
    --yt-primary-text-color: hsla(0, 100%, 100%, .88);
    --yt-hovered-text-color: hsla(0, 100%, 100%, .74);
    --yt-secondary-text-color: hsla(0, 100%, 100%, .5);
    --yt-tertiary-text-color: hsla(0, 100%, 100%, .5);
    --yt-placeholder-text-color: hsla(0, 100%, 100%, .5);
    --yt-border-color: hsla(0, 100%, 100%, .08);
    --yt-commentbox-border-inactive: hsla(0, 100%, 100%, .5);
    --yt-commentbox-border-active: hsla(0, 100%, 100%, .88);
    --yt-alert-background: hsla(0, 100%, 100%, .26);
    --yt-paper-button-ink-color: hsla(0, 0%, 100%, .2);
    --yt-icon-color: hsla(0, 100%, 100%, .5);
    --yt-icon-hover-color: hsla(0, 100%, 100%, .74);
    --yt-icon-active-color: hsla(0, 100%, 100%, .88);
    --yt-icon-disabled-color: hsla(0, 100%, 100%, .26);
    --yt-expand-color: hsla(0, 0%, 100%, .6);
    --yt-channel-owner: hsla(0, 100%, 100%, .88);
    --yt-placeholder-text: hsl(0, 0%, 93.3%);
    --yt-playlist-background-header: hsla(0, 0%, 53.3%, .4);
    --yt-playlist-background-item: hsla(0, 0%, 53.3%, .2);
    --yt-playlist-title-text: hsla(0, 100%, 100%, .88);
    --yt-playlist-message-text: hsl(0, 0%, 93.3%);
    --yt-playlist-message-text-hover: hsla(0, 100%, 100%, .74);
    --yt-subscribe-button-text-color: hsla(0, 100%, 100%, .88);
    --yt-button-active-color: hsla(0, 100%, 100%, .88);
    --yt-copyright-text: hsla(0, 0%, 100%, .6);
    --yt-guide-entry-text-color: hsla(0, 100%, 100%, .88);
    --yt-thumbnail-placeholder-color: hsl(0, 0%, 16%);
    --yt-primary-disabled-button-text-color: hsla(0, 0%, 100%, .2);
    --yt-featured-channel-title-text-color: hsla(0, 100%, 100%, .5);
    --yt-formatted-string-deemphasize-color: hsl(0, 0%, 53.3%);
    --yt-video-secondary-info-description-background: hsla(0, 0%, 53.3%, .2);
    --yt-simple-menu-header-background: hsla(0, 0%, 53.3%, .4);
    --yt-item-section-header-color: hsl(0, 0%, 93.3%);
    --yt-menu-hover-backgound-color: hsla(0, 100%, 100%, .08);
    --yt-menu-focus-background-color: hsla(0, 100%, 100%, .26);
    --yt-searchbox-text-color: var(--yt-primary-text-color);
    --yt-material-searchbox-inactive: hsl(0, 0%, 7%);
    --yt-material-searchbox-active: hsl(0, 0%, 100%);
    --yt-material-searchbox-inactive-shadow: hsla(0, 0%, 0%, .04);
    --yt-material-searchbox-active-shadow: hsla(0, 0%, 0%, .26);
    --yt-material-searchbox-inset: hsla(0, 0%, 0%, .04);
    --yt-material-searchbox-text-color: hsl(0, 0%, 6.7%);
    --yt-app-background: hsl(0, 0%, 7%);
    --yt-main-app-background: hsl(0, 0%, 7%);
    --yt-main-app-background-tmp: hsl(0, 0%, 7%);
    --yt-guide-background: hsl(0, 0%, 11%);
    --yt-dialog-background: hsl(0, 0%, 14%);
    --yt-searchbox-background: hsl(0, 0%, 7%);
    --yt-channel-header-background: hsla(0, 0%, 11%, .8);
    --yt-sidebar-background: hsla(0, 0%, 11%, .8);
    --yt-transcript-background: hsl(0, 0%, 11%);
    --yt-chat-bubble-other-border-color: rgba(255, 255, 255, 0.20);
    --yt-chat-bubble-other-background-color: transparent;
    --yt-chat-bubble-self-border-color: rgba(255, 255, 255, 0.20);
    --yt-chat-bubble-self-background-color: #282828;
}



ytd-masthead#masthead {
    background-color: black;
    opacity: .5;
    transition:all .5s ease;
}

ytd-masthead#masthead:hover {
    opacity: 1;
}

ytd-page-manager#page-manager {
    background-color: black;
}

div#columns {
    background-color: black;
}

ytd-watch-flexy.style-scope.ytd-page-manager.hide-skeleton {
    background-color: black;
}


#columns.ytd-watch-flexy {
    background-color: black;
}

.ytp-progress-list {
    background-color: #4d4d4d26;
}

.ytp-hover-progress.ytp-hover-progress-light {
    background-color: #ff00903b;
}

.ytp-load-progress {
    background-color: #ff00001a;
}

div#guide-content {
    background: black;
}

.header.ytd-playlist-panel-renderer {
    background-color: hsla(0, 0%, 0%, 0.4);
}
ytd-playlist-panel-renderer#playlist {
    border: #ffffff1c 1px solid;
}
.ytp-webgl-spherical-control {
    opacity: .05;
    filter: invert(1);
}
div#guide-inner-content {
    background-color: black;
}
div#contentContainer {
    opacity: .8;
    background-color:black;
    transition: all .5s ease;
    box-shadow: black 0 2em 4em;
}

div#contentContainer:hover {
/*     opacity: 1; */
}

ytd-guide-renderer[is-guide-refresh] #sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    border-bottom: 1px solid rgba(0, 0, 0, 0.83);
}
ytd-app {
    --app-drawer-width: 240px;
    --app-drawer-content-container_-_background-color: black;
    background: hsl(0, 0%, 0%);
}
ytd-thumbnail.style-scope.ytd-grid-video-renderer {
    border: #ffffff24 1px solid;
    border-radius:.4em;
    overflow:hidden;
}
}
