css:
@-moz-document regexp("https://hiddenelise.wordpress.com/[^/]+/[^/]+/[^/]+/chapter-.*") {
/*[[hide_wp_admin]]*/
/*[[customize_bg_fg]]*/

/* Stitch Images top/bottom together. What code is needed changes between chapters.... */
.entry-content > .wp-block-image,
.entry-content  figure > img,
.entry-content  figure,
.entry-content  img,
.entry-content {
    margin-top: 0 ;
    margin-bottom: 0 ;
    margin-block-start: 0 ;
    margin-block-end: 0 ;
    margin-inline-start: 0 ;
    margin-inline-end: 0 ;
    line-height: 0px !important;
}

/* Try not to mess up text, without introducing problems. */
.entry-content p,
.entry-content h,
.entry-content h1,
.entry-content h2,
.entry-content h3,
.entry-content h4,
.entry-content h5 {
    line-height: 0px !important;
    /* Tested on firefox only, moved text, but didnt move non text elms.*/
    margin-top: 1.2em ;
    margin-bottom: 1.2em ;
}

}

style_setting_options.value:
/* Hide ugly space taking WP bar */
#wpadminbar {
    display: none;
}

/* Chage theme to dark */
body,
body > * {
    background: /*[[main_bg_color]]*/ !important;
    color:  /*[[main_text_color]]*/;
}
