css:
/* Written and confirmed on Stylus. 
   img to small and 100% when hover */

/* smaller */
img:not([class*="logo"]):not([id*="logo"]
):not([src*="icon"]):not([class*="icon"]
):not([class*="favicon"]
):not([class*="star"]):not([id*="rating"])
{
    width: 16px !important;
    height: 16px !important;
    border-left: 2px solid orangered !important;
}

a img:not([class*="logo"]):not([id*="logo"]
):not([src*="icon"]):not([class*="icon"]
):not([class*="favicon"]
):not([class*="star"]):not([id*="rating"]),
div[style*="image"],
span[style*="image"] 
{ 
    background-size: 16px !important;
    background-repeat: no-repeat;
    border-left: 2px solid orangered !important;
}

/* hover */
img:not([class*="logo"]):not([id*="logo"]
):not([src*="icon"]):not([class*="icon"]
):not([class*="favicon"]
):not([class*="star"]):not([id*="rating"]):hover
{
    width: 100% !important;
    height: 100% !important;
}

a img:not([class*="logo"]):not([id*="logo"]
):not([src*="icon"]):not([class*="icon"]
):not([class*="favicon"]
):not([class*="star"]):not([id*="rating"]):hover,
div[style*="image"]:hover,
span[style*="image"]:hover
{
    background-size: 100% !important;
}
