css:
@-moz-document url("https://ishadijcks.github.io/") {

.col-lg-12 {

   border-radius: 15px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

   display: block;
   
   border-style: hidden;

}

#console {

   padding: 5px;

   border-color: rgba(0, 0, 0, 0);

   border: 0px solid black;

   background-color: rgb(69, 70, 71);

   margin: 0 0 15px 0 !important;

   overflow-x: hidden;

}

.oakItem {

   border-radius: 15px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

.oakItemImage {

   border-radius: 15px;

   background-color: rgb(203, 211, 219);

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

#pokemons {

   border-radius: 5px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);
   
   border-style: hidden;

}

#items {

   border-radius: 5px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);
   
   border-style: hidden;

}

table {

   background-color:rgb(40, 43, 45);

   border-radius: 5px;

   border: 0px solid black;

   border-color: rgba(0, 0, 0, 0) !important;
   
   border-style: hidden;

}

table, th, td {

   border-collapse: collapse;

   border: 1px solid rgba(0, 0, 0, 0);

   border-color: rgba(0, 0, 0, 0) !important;

   color: rgb(203, 211, 219);

   padding: 0 5px;

}

#map {

   display: block;

   border-radius: 15px;

   border-color: rgba(0, 0, 0, 0) !important;
   
   border-style: hidden;

}

#mapDiv {

   margin: 0 0 15px 0;

}

#sortBox {

   border-radius: 5px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

.tooltipShard {

   border-color: rgba(0, 0, 0, 0);

   background-color: rgb(28, 34, 34);

}

.modal-header {

   background-color: rgb(40, 43, 45);

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

#energyDisplay {

   color: rgb(203, 211, 219) !important;

}

a {

   color: rgb(150, 30, 6);

}

h4 {

   font-weight: bold;

}

.modal-body {

   background-color: rgb(40, 43, 45);

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

.progress {

   border-radius: 5px;

   background-color: rgb(40, 43, 45);

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);

}

#questCounter {

   border-radius: 15px;

   background-image: linear-gradient(35deg, rgb(69, 70, 71), rgb(40, 43, 45));

   color: rgb(203, 211, 219);

   border-color: rgba(0, 0, 0, 0);
   
   border-style: hidden;
   
   padding: 5px;

}

#eggs {

   background: rgb(40, 43, 45) !important;

   border-radius: 15px;

   border-color: rgba(0, 0, 0, 0) !important;
   
   border-style: hidden;
   
   display: block;

}

tr:nth-child(even) {

   background: rgb(50, 53, 55) !important;

}

tr:nth-child(odd) {

   background: rgb(69, 70, 71) !important;

}

::-webkit-scrollbar {

   width: 10px;

}

::-webkit-scrollbar-track {

   border-radius: 5px;

   background: rgb(40, 43, 45);

}

::-webkit-scrollbar-thumb {

   border-radius: 3px;

   background: rgb(80, 80, 80);

}

::-webkit-scrollbar-thumb:hover {

   background: #555;

}

html {

   height: 100%;

   width: 100%;

   background-image: linear-gradient(to bottom, #79c9fa 20%, #7dad71 21%);

   background-size: 100%;

}

body {

   background-position: center;

   background-repeat: no-repeat;

   background-size: 100%;

   background-color: rgba(0, 0, 0, 0);

   overflow: auto;

}

.shardUpgradeLocked {

   opacity: .3;

   border-color: rgba(0, 0, 0, 0);

}

.shardUpgrade {

   border-color: rgba(0, 0, 0, 0);

}

.shinyFiller {

   border-radius: 50px;

}
.smallShinyFiller{

   border-radius: 50px;

}

#map {

   color: rgb(40, 43, 45);

}

.rank0 {

   background-color:rgba(0, 0, 0, 0);

}

.rank1 {

   background-color:rgba(40, 40, 40, 100);

}

.rank2 {

   background-color:rgba(30, 30, 30, 100);

}

.rank3 {

   background-color:rgba(20, 20, 20, 100);

}

.rank4 {

   background-color:rgba(10, 10, 10, 100);

}
.rank5 {

   background-color:rgba(0, 0, 0, 100);

}

button {

   margin: 5px;

}

.useItemButton{
	
   border-radius: 5px;
   border-color: rgba(0, 0, 0, 0);

}

#currentEnemy{
   
   padding: 5px;
   
	border-style: hidden;

}

#statDiv{

   border-style: hidden;

}

#navBar{

	border-style: hidden;

}
}
