css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.youtube.com/watch),url-prefix(http://au.youtube.com/watch) {
#watch-this-vid, #watch-player-div, #movie_player {width: 960px !important; height: 565px !important;} #watch-player-div {padding: 0px !important;} #watch-other-vids, #watch-channel-brand-div {margin-top: 10px !important;} #watch-channel-brand-div { display: none !important }
body {background: #161616 !important;}
#watch-video-response, #watch-ratings-views, .watch-comment-time, #watch-promoted-videos-container, #copyright, #chrome-promo, #watch-promoted-vid, #watch-promoted-container, span.expand-header, .watch-arrow, #adblitz-promo-hdr, #adblitz-promo-ugc, .alignC, #watch-stats-data-wrapper, .QLIconImg, #watch-promoted-videos-container, #logo, #lang-locale-picker-links-wrapper, #slogan, #region-and-language-picker-links-wrapper, #watch-more-popular, #footer  {display:none !important;}

*{font-size: 11px !important; color: #aaaaaa !important; font-family:  tahoma !important;}

#watch-tab-share-body, #watch-main-area, .watch-comment-head, .watch-comment-pagination, #watch-channel-vids-div, #watch-channel-vids-body, #watch-related-vids-body, #watch-promoted-videos-container, #footer, #bar, #watch-comments-stats, #watch-channel-vids-div, #watch-channel-videos-panel, #watch-related-videos-panel  {background: #111111 !important;}

.watch-comment-pagination, .watch-comment-entry{border: 0px !important;}

#watch-main-area, #watch-tabs master-sprite, .watch-comment-head, .watch-comment-entry-reply, #watch-channel-vids-div, #watch-channel-vids-body, #watch-related-vids-body, #watch-promoted-videos-container, #footer, .links, #watch-comments-stats, #watch-channel-vids-div, #watch-channel-videos-panel, #watch-related-videos-panel {border: 1px solid #222222 !important;}

#watch-channel-discoverbox, #watch-related-discoverbox, #watch-other-vids {height: 250px !important; float:left !important;}

#watch-this-vid-info {float: right !important; margin-top: 10px !important;}

.search-form, #search-type-masthead, #masthead-search-term, .masthead-button-wrapper, .yt-button, .yt-button-primary, .nav-item, .master-sprite, .search-term-focus {background:#111111 !important;}

.nav-item {border:none !important;}
#bar {border:1px solid #222222 !important;}
#bar a:hover {color:#494949 !important;}
#bar a:visited {color:#494949 !important;}
.confirmBox {background:#262626 !important;border:1px dashed #222222 !important;color:green !important;}
.errorBox {background:#262626 !important;border:1px dashed red !important;color:#7a7a7a !important;}

form.search-term {background:#262626 !important; font-weight:bold !important; font-size:12pt !important; padding-bottom:3px !important; border:1px solid #111111 !important;}
.search-term {color:#ffffff !important; font-weight:bold !important; padding-left:5px!important;}
.yt-button, .yt-button-primary {background:#111111 !important; border:1px solid #222222 !important; text-decoration:none !important;}





}
