css:
@-moz-document domain("v3rmillion.net")
{
  #wrapper
  {
    background: #2e3136;
  }

  .tcat
  {
    background: #1e2124;
  }

  .pm_new,
.alert_new
  {
    background: #4a4c51!important;
  }

  #logo img
  {
    content: url("https://i.imgur.com/LBWFOeR.png");
  }

  .inline_row a,
.inline_row a:visited
  {
    color: #fff;
  }

  #content,
.navigation,
#header
  {
    background: #1e2124;
    border: 1px solid #1e2124;
  }

  ul.menu li a.active,
ul.menu li ul li a:link,
ul.menu li ul li a:visited
  {
    background: #4a4c51!important;
  }

  .panel_buttons
  {
    background: #4a4c51;
    border-top: 5px solid #4a4c51;
  }

  .trow_sep
  {
    background: #4a4c51;
    color: #99aab5;
  }

  ul.menu li ul
  {
    background: #4a4c51;
    border-top: 5px solid #4a4c51;
  }

  .ddm_anchor:hover,
.ddm_anchor a:hover,
ul.menu li a:hover,
ul.menu li:hover
  {
    background: #4a4c51!important;
  }

  .usercp_nav_item:before,
.modcp_nav_item:before
  {
    color: #4a4c51;
  }

  .profile_header
  {
    background: #2e3136;
  }

  fieldset
  {
    border: 1px solid #4a4c51;
  }

  .thead input.textbox,
.thead select
  {
    border: 1px solid #4a4c51;
  }

  #bridge
  {
    background: #2e3136;
    border-bottom: 5px solid #4a4c51;
  }

  .trow1,
.trow2
  {
    background: #2e3136;
    border-bottom: 1px solid #1e2124;
  }

  .forum_on,
.cRem a:visited,
.cRem a:link
  {
    color: #4a4c51!important;
  }

  .forum_off
  {
    color: #636363;
  }

  .thead,
.tfoot
  {
    background: #4a4c51;
  }

  .buttons
  {
    background: #1e2124;
    border: 1px solid #1e2124;
  }

  .buttons
  {
    background-color: #1e2124!important;
    border: 1px solid #4a4c51!important;
  }

  a.button:link,
a.button:visited,
a.button:active,
.pagination a,
button,
input.button,
.postbit_buttons>a:link,
.postbit_buttons>a:hover,
.postbit_buttons>a:visited,
.postbit_buttons>a:active
  {
    background: #4a4c51;
    border: 1px solid #4a4c51;
  }

  .thread_head
  {
    background: #2e3136!important;
    border-bottom: 5px solid #4a4c51!important;
  }

  .post .post_head
  {
    border-top: 15px solid #2e3136;
  }

  .post .post_author
  {
    border-bottom: 2px solid #2e3136;
    background: #2e3136;
  }

  blockquote
  {
    border: 1px solid #1e2124;
    background: #011015;
  }

  blockquote cite
  {
    border-bottom: 1px solid #2e3136;
  }

  .post .post_author div.author_statistics
  {
    background: #031c23!important;
  }

  .post_controls
  {
    background: #2e3136;
    border-bottom: 1px solid #2e3136;
    border-top: 3px solid #2e3136;
  }

  ul.bottommenu
  {
    background: #2e3136;
  }

  .tborder
  {
    background: #01151b;
  }

  #footer
  {
    background: #1e2124;
    border-top: 5px solid #4a4c51;
  }
}
