css:
@-moz-document domain("www.ettv.tv"), domain("www.ettv.to"), domain("www.ettvdl.com"), domain("www.ettvcentral.com"), domain("www.ettv.be") {
body {
    background-color: #212121 !important;
    color: #fafbfc !important;
    pointer-events: none
}
a,
b,
p,
strong,
td,
span,
img,
input,
select,
button {
    pointer-events: auto
}
select,
input[type=text],
textarea {
    -moz-appearance: none !important;
    background-color: #25272a !important;
    border-color: #343434 !important;
    color: #eee !important
}
.main_wrapper .left_column,
.vp_ab div,
.vp_ab,
.myFrame-foot,
.myFrame-caption,
.download_links_a,
.download_liinks_a,
fieldset.download legend,
#downloadbox table tr td:first-child,
.header .tagline,
.header .logo img,
.myFrame-content div center form br {
    display: none
}
.myFrame-content,
#downloadbox {
    background-color: #25272a !important;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    border-radius: 10px;
    border: 0px
}
.header {
    min-height: 60px
}
.header .logo::after {
    content: "Ettv";
    color: #c0c0c0;
    font-size: 26px;
    font-weight: bold;
    letter-spacing: 1px
}
.header .logo:hover {
    text-decoration: none
}
ul li a,
a,
a b,
.header .logo:hover::after {
    color: #93bcd1 !important
}
ul li {
    overflow: hidden
}
ul li a:hover,
ul li a:focus,
a:hover,
a:hover b {
    color: #a0b4c8 !important
}
.search_cloud,
.table_head {
    background-color: #111;
    box-shadow: none;
    border: 0px
}
.search_cloud a {
    color: #c0c0c0 !important
}
.main_wrapper .right_column,
.data_group .imdb {
    width: 100% !important;
    max-width: 100%
}
.data_group .imdb .download {
    float: left
}
.data_group .imdb > center {
    float: left;
    width: 50%
}
.data_group .imdb > fieldset {
    width: 50%
}
#downloadbox,
fieldset.download {
    margin-top: 0px !important;
    border: 0px
}
table {
    border: 0px !important
}
table.table.table-hover.table-bordered tr:hover,
.table_table tr:hover {
    background-color: #111 !important
}
table.table.table-hover.table-bordered tr:nth-child(even),
.table_table tr:nth-child(even) {
    background-color: #212121
}
table td,
table th {
    border: 0px !important;
    background-color: transparent !important;
    background-image: none !important
}
.navigation {
    background-color: #24292e
}
b,
strong,
table tr th {
    color: #fafbfc !important
}
.table_table td,
.table_table th {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    line-height: 24px;
}

/*  */
table tr th:nth-child(5) {
    position: relative
}
table tr th:nth-child(5) img {
    display: none
}
table tr th:nth-child(5)::before {
    content: '';
    width: 22px;
    height: 14px;
    background-color: #62cfc1;
    border-radius: 4px;
    position: absolute;
    top: 9px;
    left: -1px
}
table tr th:nth-child(5)::after {
    content: "";
    display: block;
    position: absolute;
    left: 9px;
    bottom: 7px;
    border-left: 8px solid transparent;
    border-right: 1px solid transparent;
    border-top: 7px solid #62CFC1
}
table tr th:nth-child(6) img {
    display: none
}
table tr th:nth-child(6):after {
    content: '';
    float: left;
    border-left: 13px solid transparent;
    border-right: 13px solid transparent;
    border-bottom: 20px solid green
}
table tr th:nth-child(7) img {
    display: none
}
table tr th:nth-child(7):after {
    content: '';
    border-left: 16px solid transparent;
    border-right: 13px solid transparent;
    border-top: 20px solid #f00;
    float: left
}
}
