css:
@-moz-document domain("godoc.org") {

body {
    background-color: #263238;
    color: white;
    font-family:'Fira Sans';  
}

.container {
    width:100%;
    max-width:100%;
}



a, .navbar-brand strong {
    color:#96d6fc;
}

a, .navbar-brand strong {
    color:#96d6fc;
}

.list-unstyled {
    margin-left:1em;
}

.navbar {
    top:0em;
    width:100%;
    z-index:100;
}

code {
    font-family: 'Fira Mono';
    color:white;
    background-color:#222d32;
    padding:1em;
    margin:0em;
    margin-left:-2em;
    border-radius:2em;
}

h1,h2,h3,h4,h5,h6 {
    color:#fff;
}

h1, h2, h5, h6 {
    padding-top:1em;
    font-family: 'Fira Sans Black';
}

.navbar-nav {
    margin:0em;
    padding:0em;
}

h4, h3 {
    padding:0em;
    padding-top:1em;
    margin:1em 0em 0em 0em;
    z-index:1;
    font-family: 'Fira Mono';
}

h4 a, h3 a {
    font-size: 133%;
}

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
    padding-top:1em;
    padding-left:0em;
    font-size:133%;
    font-family: 'Fira Mono';
}

input {
    padding:1em;
    margin:1em;
}

#x-projnav {
    background-color:#222d32;
    border-radius:2em;
    padding:1em;
    padding-left:1.4em;
    margin:0em;
    margin-left:0.5em;
    margin-right:0.5em;
}

.navbar-default {
    background-color:#222d32;
    border:0em;
    border-radius:0em;
    padding:0em;
}

pre {
    color:#aaa;
    background-color:#222d32;
    border:0px;
    margin-bottom:2em;
    padding:1em;
    padding-top:1.2em;
    padding-left:1.4em;
    border-radius:1em;
    font-size:0.8em;
    margin:1em auto;
    margin-left:2em;
    width:80%;
}

.navbar-brand::before {
    content:  "ðŸ“–";
    display: block;
    margin:0px auto;
    float: left;
}

.jumbotron {
    background-color:#222d32;
    color:#aaa;
}

.highlighted {
    border:0px solid #888;
    margin:1em auto;
    background-color:#29353b;
    padding-bottom:0em;
    border-bottom:1px solid #111;
}

.highlighted::after {
    content:"<--"
}

.permalink {
    font-size:1em;
}

ul, ol {
    margin:0em;
    padding:1em;
}

li {
    margin:0em;
}

ul li {
    list-style-type:none;
}

p {
    color:#ccc;
    margin:2em;
}

#x-footer {
    background-color:#29353b;
    border:0em;
}
}
