css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://antwrp.gsfc.nasa.gov/apod/"),
url-prefix("http://antwrp.gsfc.nasa.gov/cgi-bin/apod/"),
url-prefix("http://apod.nasa.gov") {


body {
  background: #000 !important;
  color: #DDD !important;
  width: 920px !important;
  margin: 20px auto;
  font-family:  Palatino, 'Palatino Linotype', Times, 'Times New Roman', serif;
  font-size: 13px;
  line-height: 20px;
}

center {
  text-align: left;
}

h1 {
  color: #FFF;
  font-weight: normal;
  padding-bottom: 12px;
  border-bottom: 4px double #333;
  font-variant: small-caps;
  letter-spacing: 0.35em;
}

p {
  width: 600px;
  margin-left: 60px;
}

b {
  font-style: italic;
  font-size: 16px;
  color: #FFF;
}

a {
  text-decoration: none !important;
  color: #FD6 !important;
}
a:hover {
  color: #46E !important;
  text-decoration: underline !important;
}
a:visited { color: #D62 !important; }


/* Discover the cosmos */
h1 + p {
  font-size: 12px;
  line-height: 18px;
  width: 520px;
  padding-left: 400px;
  margin-left: 0;
}

/* Date */
h1 + p + p {
  margin-top: -40px;
  font-size: 36px;
  font-style: italic;
  color: #666;
  width: auto;
  margin-left: 0;
}

h1 + p + p + * {
  text-align: center;
  padding: 0;
}

a img {
  display: block;
  margin: 30px auto 0;
  border: 1px solid #222 !important;
}
a:hover img {
  border-color: #666  !important;
}


/* picture title */
center + center b:first-child {
  font-size: 28px;
  font-style: normal;
  line-height: 36px;
}

hr {
  border: none;
  border-bottom: 1px solid #222;
}

hr + a,
hr + a + a,
hr + a + a + a,
hr + a + a + a + a,
hr + a + a + a + a + a,
hr + a + a + a + a + a + a,
hr + a + a + a + a + a + a + a,
hr + a + a + a + a + a + a + a + a,
hr + a + a + a + a + a + a + a + a + a,
hr + a + a + a + a + a + a + a + a + a + a {
  padding: 4px;
  color: #FFF !important;
  background: #2B2B2B;
  font-weight: bold;
  font-size: 11px;
  -moz-border-radius: 3px;
  margin: 0 4px;
  text-transform: uppercase;
  letter-spacing: 0.1em;
}
hr + a {
}

hr + a:hover,
hr + a + a:hover,
hr + a + a + a:hover,
hr + a + a + a + a:hover,
hr + a + a + a + a + a:hover,
hr + a + a + a + a + a + a:hover,
hr + a + a + a + a + a + a + a:hover,
hr + a + a + a + a + a + a + a + a:hover,
hr + a + a + a + a + a + a + a + a + a:hover,
hr + a + a + a + a + a + a + a + a + a + a:hover {
  background: #26A;
  color: #FFF !important;
  text-decoration: none !important;
}



}
