css:
@-moz-document domain("www.vn-meido.com") {
/*Custom Colours!*/
:root {
    --background1: #3f3f3f; /*Default: ##3f3f3f*/
    --background2: #222; /*Default: #222*/
    --background3: #444; /*Default: #444*/
    
    --headerBG: #212121; /*Default: #212121*/
    
    --highlight: #ff6e00; /*Default: #ff6e00*/
    
    --link-font-colour: #ff6e00; /*Default: #ff6e00*/
    --text-font-colour: #ccc; /*Default: #ccc*/
    --text-font-colour2: #888; /*Default: #888*/
    
    --box-roundness: 5px; /*Default: 5px, 10 is more like default theme*/
}
/*
  ▄   ▄
 ▄██▄▄██▄          
 ███▀██▀██            ╔╗ ┌─┐┌┐┌┌─┐█
 ▀███████▀            ╠╩╗├─┤│││├┤ █
   ▀███████▄▄         ╚═╝┴ ┴┘└┘└─┘█
    ██████████▄ ▀ ▀ ▀ ▀▀▀▀▀▀▀▀▀▀▀▀█▀
  ▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀▀█▀ 
*/

/*Admin Chat*/
#nchat_admin_shoutbox{
    background-color: var(--background2) !important;
    padding-left: 5px;
    display: block;
    width: inherit !important;
    border-left-width: 0px !important;
    border-radius: var(--box-roundness);
}
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: var(--background3); }
::-webkit-scrollbar-thumb:hover { background: var(--background1); }

body {
    background: var(--background1);
}
#header {
    background-color:var(--background1);
    padding-left: 0px;
}
#header div.frame {
    background-color: var(--background1);
    background: var(--background1);
    display: block;
    padding: 5px 0px 0em 0;
    color:  var(--text-font-colour);;
}
#top_section {
    background-color:var(--background1);
    margin-bottom: 0px;
    color:  var(--text-font-colour);;
}

/*Links and Search*/
a:link, a:visited {
    color: var(--link-font-colour) !important;
}
input, button, select, textarea {
    font: 95%/115% verdana, Helvetica, sans-serif;
    color:  var(--text-font-colour);;
    background: var(--background2);
    border: 1px solid var(--highlight);
;
    padding: 2px;
}
.button_submit, .button_reset {
    background: var(--background2);
    border: 1px solid var(--highlight);
}
#verification_image_post{
    -webkit-filter: invert(100);
}
div.title_bar {
    background: var(--background2);
    border-radius: var(--box-roundness);
}
h4.titlebg, h3.titlebg {
    background: var(--background2);
}

/*navbar*/
dropmenu a {
    color:  var(--text-font-colour);;
}
.navigate_section ul {
    border-top: 1px solid var(--highlight);
}
.dropmenu a {
    display: block;
    color:  var(--text-font-colour);;
    text-decoration: none;
    background-color:var(--background1);
}
.dropmenu li li {
    border-left: 1px solid var(--highlight);
    border-right: 1px solid var(--highlight);
}
.dropmenu li ul {
    border-bottom: 1px solid var(--highlight);
    background-color:var(--background1);
    background: var(--background1);
}
.dropmenu li li a:hover, .dropmenu li li:hover>a {
    background: var(--background3);
}

/*Rounded Buttons*/
.dropmenu li a.active span.firstlevel {
    border-radius: var(--box-roundness);
}
.dropmenu li a.firstlevel span.firstlevel {
    height: 19px;
    border-radius: var(--box-roundness);
}
.dropmenu li a.firstlevel:hover, 
.dropmenu li:hover a.firstlevel {
    border-radius: var(--box-roundness);
    background: var(--background3);
}
.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel {
    background: var(--background3);
}
.dropmenu li a.active {
    border-radius: var(--box-roundness);
}
/*Button List (reply and all that)*/
.buttonlist ul li a span {
    border-radius: var(--box-roundness);
}
.buttonlist ul li a.active, 
.buttonlist ul li a.active span{
    border-radius: var(--box-roundness);
    background: var(--background2);
}
.buttonlist ul li {
    border-radius: var(--box-roundness);
}
.buttonlist ul li a {    
    border-radius: var(--box-roundness);
}
/*NEW! icon*/
img.new_posts {
    border-radius: var(--box-roundness);
}
[id*="newicon"] > img {
    border-radius: var(--box-roundness);
}
/*Post Count Bar Thing*/
.statsbar div.bar {
    border-radius: 10px !important;
}
.statsbar div.bar div {
    border-radius: 10px !important;
}
.statsbar div.bar div {
    position: relative;
    right: 0px;
    padding: 0px 0px 0 0;
    height: 16px;
}
.statsbar div.bar {
    background: var(--background1);
}

/**/
h1, h2, h3, h4, h5, h6 {
    color:  var(--text-font-colour);;
}
h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a {
    color:  var(--text-font-colour) !important;
}
#main_menu {
    padding: 0px 2em;
    width: 95%;
}
#content_section {
    background-color:var(--background1);
    padding-left: 20px;
    background: var(--background1);
}
#content_section div.frame {
    background: var(--background1);
}

/*Chat Box*/
span.upperframe {
    background-color:var(--background1);
}
.roundframe {
    background-color:var(--background1);
    border-left: 0px solid var(--highlight);
    border-right: 0px solid var(--highlight);
    padding-bottom: 10px;
    border-radius: var(--box-roundness);
}
span.upperframe span {
    height: 0px;
    background-color:var(--background1);
}
div.roundframe div.cat_bar h3.catbg {
    background: var(--headerBG);
    border-radius: var(--box-roundness);
}
div.cat_bar {
    background: var(--headerBG) !important;
    padding-left: 0px;
    height: 25px;
    margin-bottom: 0px;
    border-radius: var(--box-roundness);
}
div.title_barIC {
    background:var(--background1);
    padding-left: 0px;
    height: 27px;
    overflow: hidden;
    margin-bottom: 1px;
    border-radius: var(--box-roundness);
}
div.title_barIC h4.titlebg {
    background:var(--background1);
    color: var(--text-font-colour);
    padding-left: 5px;
}
.windowbg, #preview_body {
    color:  var(--text-font-colour);;
    background-color:var(--background1);
}
span.lowerframe {
    display: none;
    background-color:var(--background1);
    padding-left: 00px;
}

/**/
body, td, th, tr {
    color:  var(--text-font-colour2);;
    background-color:var(--background1);
}
h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
    background: var(--headerBG);
    padding-right: 5px;
    padding-left: 5px;
    padding-bottom: 10px;
    height: 15px;
}
.buttonlist ul li a {
    background: var(--background2);
    padding: 0 0 0 5px;
    color:  var(--text-font-colour);;
}
.buttonlist ul li a span {
    background: var(--background2);
}

/*Footer*/
#upshrinkHeaderIC p.last {
    margin: 0;
    padding: 0.5em;
    border-top: 2px dotted var(--highlight);
}
#footer_section {
    text-align: center;
    background:var(--background1);
    padding-left: 20px;
}
#footer_section div.frame {
    background: var(--background1);
    display: block;
    padding: 20px 0 0 0;
    color: var(--highlight);
}
a.new_win:link, a.new_win:visited {
    color: var(--link-font-colour);
}

/*Forum Post Stuff*/
.windowbg span.topslice {
    display: block;
    padding-left: 0px;
    background: var(--background1);
    border-radius: var(--box-roundness);

}
.windowbg span.topslice span {
    display: block;
    background: var(--background1);
    height: 20px;
}
.windowbg span.botslice span {
    display: block;
    background: var(--background1);
    height: 0px;
}
.windowbg2 span.topslice {
    display: block;
    background: var(--background3);
    height: 20px;
    border-radius: var(--box-roundness);
}
.windowbg2 span.topslice span {
    display: block;
    background-color:var(--background3);
    height: 0px;
    border-radius: var(--box-roundness);
}
.windowbg2 {
    color:  var(--text-font-colour);;
    background: var(--background3);
        border-radius: var(--box-roundness);

}
.windowbg2 span.botslice {
    padding-left: 0px;
    background: var(--background3);
    border-radius: var(--box-roundness);
    height: 20px;
}
.windowbg2 span.botslice span {
    display: block;
    background: var(--background3);
    height: 11px;
    border-radius: var(--box-roundness);
}
fieldset legend {
    font-weight: bold;
    color:  var(--text-font-colour);;
}
div.spoilerbody {
    color:  var(--text-font-colour);;
    border: 1px dotted gray;
    padding: 1em;
    margin: 1em;
}
.bbc_tt {
    color:  var(--text-font-colour);;
}
fieldset.spoiler {
    border: 1px dashed var(--highlight);
}
blockquote.bbc_standard_quote {
    background: var(--background2);
    color:  var(--text-font-colour);
}
blockquote.bbc_alternate_quote {
    background: var(--background1);
    color:  var(--text-font-colour);
}
.inner {
    padding: 1em 1em 2px 0;
    margin: 0 1em 0 0;
    border-top: 1px solid var(--highlight);
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    border-top: 2px solid var(--highlight);
    border-bottom: 2px solid var(--highlight);
}
.description, .description_board, .plainbox {
    border: 1px solid var(--highlight);
    background-color: var(--background1);
}
hr, .hrcolor {
    height: 1px;
    background: linear-gradient(
        to top, 
        rgba(0, 0, 0, .14), 
        rgb(0, 0, 0) 
        )
    var(--highlight);
}

/*Subject List Stuff*/
.topic_table table thead {
    border-bottom: 1px solid var(--highlight);
}
.table_grid tr.catbg, .table_grid tr.titlebg {
    border-bottom: 1px solid var(--highlight);
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th {
    color: #fff;
    background:var(--background1);
}
tr.catbg th.first_th {
    background:var(--background1);
}
tr.catbg th.last_th {
    background:var(--background1);
}
table.table_grid td {
    padding: 3px;
    border-bottom: 1px solid var(--background2);
    border-right: 1px solid var(--background2);
    border-radius: unset;
}
.table_frame .table_list td.icon, .table_frame .table_list td.info, .table_frame .table_list td.stats {
    border-right: 2px solid var(--highlight);
}
.topic_table td.lockedbg2,
.lockedbg,
.lockedbg2 {
    background:var(--background2);
}
.stickybg {
    background: var(--background2);
}
.stickybg2 {
    background: var(--background2);
}
.bbc_img {
    border: 0;
    max-width: 80%;
}

/*Calendar*/
.calendar_today {
    background-color: var(--background3);
    border-radius: var(--box-roundness);
    
    color: var(--text-font-colour);
    font-size: 25pt;
    
    vertical-align: middle !important;
}
#main_grid table.weeklist td.weekdays,
#main_grid table.weeklist td.windowbg {
    border-bottom: 2px solid var(--background2);
    border-radius: 0;
}
.holiday{
    color: var(--text-font-colour);
    font-size: 12pt;
}
.birthday {
    color: var(--text-font-colour2);
}

/*Members*/
#mlist table tbody td.windowbg2{
    border-radius: unset;
}
}
