css:
@-moz-document domain("minegate.eu"), domain("mmpos.eu") {
/* git.mmpso.eu specific configuration */

.nav-link {
    color: #efefef !important;
    padding: 5px 10px !important;
    border-radius: 5px;
}

.nav-link > .project-stat-value {
	color: #efefef !important;
}

.nav-link > svg {
	color: white !important;
}

.gpg-status-box {
	padding: 6px 12px !important;
}

.gpg-status-box.valid {
	color: #f7f7f7 !important;
    background: #77e371 !important;
}

.gpg-status-box.invalid {
	color: #f7f7f7 !important;
    background: #e37d71 !important;
}

.gl-button {
	color: #7f7f7f !important;
}

.content-block, .files-changed-inner, .commit-stat-summary,
.nav-sidebar,
.td-layout-nav-content, body, html {
    background: #2e2e2e !important;
}

.fly-out-list {
	background: #4e4e4e !important;
}

.fly-out-list li:hover a {
	background: #2e2e2e !important;
}

/* =========================================== */

.app-default-color,
.home-panel-title,
.mat-menu-item,
.mat-select-value span,
.mat-card-subtitle {
    color: #fff !important;
}

.search {
    margin-left: 10px;
}

a {
    color: #fff !important;
    text-decoration: none !important;
}

.count-badge a,
.count-badge span {
	color: #7f7f7f !important;
}

label, input, span {
    color: #f5f5f5 !important;
}

.bar {
    border-bottom: 0 !important;
}

.file-header-content,
.mat-list-text,
.mat-header-row, .mat-header-cell, .mat-cell,
.mat-tab-label-content,
.mat-hint {
    color: #fff !important;
}

.file-header-content svg {
	color: white !important;
}

article.file-holder > *,
.blob-content > *,
.line-numbers,
.line-numbers > *,
.files div {
    background: #3e3e3e !important;
}
.tree-item tr:hover,
.idiff { background: rgba(0,0,0,.3) !important; }

.diff-content div table td,
.diff-content div table tr {
	background: rgba(0,0,0,.5) !important;
    color: #fff !important;
    z-index: 0;
}

.diff-content div table td .old,
.diff-content div table tr .old {
    background: rgba(255,0,0,.3) !important;
}

.diff-content div table td .new,
.diff-content div table tr .new {
    background: rgba(0,255,0,.3) !important;
}

.form-inline,
.btn + .ide-edit-button,
.btn-sign-in,
.inline-parallel-buttons a,
.disabled-comment,
.commit-info a,
.header-action-buttons a,
.toggle-sidebar-button,
.dropdown-menu-toggle,
.dropdown-menu,
.nav-link,
.info-well,
.mat-toolbar,
.mat-toolbar-single-row {
    background: #3e3e3e !important;
    color: #fff !important;
}

.btn > svg {
	color: #333 !important;
}

.mat-select-search-panel {
	border-radius: 0 !important;
}

.mat-select-search-input,
.mat-select-search-input::placeholder,
.mat-select-26-panel .pad-sm,
.mat-sidenav-content,
.mat-sidenav {
    color: #f7f7f7 !important;
	background: #3e3e3e !important;
}

.mat-card {
    box-shadow: 0 3px 7px 2px rgba(0,0,0,.3) !important;
    background: #4f4f4f !important;
    color: #fff !important;
    margin-bottom: 15px !important;
}

.mat-tab-body .mat-card {
    background: #5a5a5a !important;
/*     box-shadow: 0 1px 3px 0 #000 !important; */
    margin-bottom: 8px !important;
}

.mat-table,
.mat-card-content,
.mat-dialog-container,
.mat-paginator {
    color: #f7f7f7 !important;
	background: #4f4f4f !important;
    border: none !important;
}

.mat-toolbar {
    box-shadow: 0 3px 3px 1px rgba(3,169,244,.6) !important;
}


.mat-menu-panel {
    background: #3e3e3e !important;
    border-radius: 15px !important;
}

.mat-option:hover {
    background: rgba(3,169,244,.8) !important;
    transition: 1s background !important;
}
.mat-menu-item:hover {
    background: rgba(3,169,244,.6) !important;
    transition: 1s background !important;
}

.mat-raised-button,
.js-view-file,
.js-toggle-diff-comments,
.mat-option {
    background: #5a5a5a !important;
}

.gt-sm span:last-child {
    color: #03a9f4 !important;
}

.mat-raised-button.mat-primary,
.mat-button:hover { 
    background: #03a9f4 !important;
    transition: 1s background !important;
}


.mat-tab-header-pagination-chevron {
    border-color: #f5f5f5 !important;
}

.mat-dialog-actions {
    margin-top: 8px;
    padding-top: 0;
    border-top: 2px solid #2f2f2f;
}

.mat-stroked-button.mat-button-base {
	border-color: inherit;
}

.mat-dialog-actions button  {
    background: #5a5a5a;
	margin-top: 10px;
    border: 1px solid #5a5a5a !important;
    box-shadow: 0 2px 3px 1px rgba(0,0,0,.3) !important;
    transition: all .3s ease;
}

.mat-dialog-actions button:hover {
	color: #f7f7f7 !important;
    background: rgba(3,169,244,.8) !important;
    box-shadow: 0 2px 5px 3px rgba(0,0,0,.15) !important;
    border: 1px solid rgba(3,169,244,.8) !important;
    transform: translateY(-2px);
}

.mat-dialog-actions button:active {
	box-shadow: none;
    transform: translateY(-1px);
}

.js-file-title:before {
    background: #2e2e2e !important;
}

.ngx-charts text {
	fill: #f7f7f7 !important;
}

.fa-logo {
	color: rgba(3,169,244,.8) !important;
}

.fa-microchip {
	color: orange;
}

.fa-bolt {
    color: yellow;
}

.fa-amd {
    color: #ED1C24;
}

.fa-nvidia {
    color: #76b900;
}

@media (max-width: 768px){
    .td-layout-nav-content{
        padding-top: 15px;
    }
    .td-layout-nav-content .mat-toolbar{
        box-shadow: 0 0 0 0 #fff !important;
        background: #2e2e2e !important;
    }
}
}
