css:
@-moz-document domain("next.voz.vn") {
body {
    font: 14px -apple-system, BlinkMacSystemFont, Segoe UI, Helvetica Neue, Arial, Helvetica, Geneva, sans-serif;
    background-color: #fff;
}
.boxes--menu-list {
    background-color: #fff;
}
.content__main-column .content {
    background-color: #fff;
}
.content__main__wrap {
    background-color: #fff;
    -webkit-box-shadow: unset;
    box-shadow: unset;
}
.header {
    color: hsla(0, 0%, 100%, .6);
    background-color: #000;
    height: 48px;
}
.header .user-cp__item {
    margin-top: -6px;
}
.brand-list ul li ._img {
    position: absolute;
    width: 24px;
    height: 24px;
    left: 30px;
    margin-top: 3px;
}
.brand-list a {
    color: #201F25;
    margin-left: 35px;
    padding: 6px 0;
}
.brand-list a strong {
    font-weight: normal;
}
.content__sidebar ul li {
    padding: 0;
}
.content__sidebar ul li:hover {
    background: #f9f9f9;
}
.brand-list ul li a:hover {
    background: #f9f9f9;
}
.brand-list ul li {
    margin-bottom: 0;
}
.logo {
   display: none;
}
.header__thread-add {
    float: right;
    margin: 13px 15px 0 0;
    color: #fff;
}
.header__thread-add svg {
    display: none;
}
.search-form ._icon-search {
    top: 14px;
}
.comment-list .heading, .comment-list .alert-wrap, .comment-list .comment-list__pagination-top, .comment-list .comments__item, .comment-list .comments__item-wrap {
    background: #fff;
}
.comment-list__pagination-top {
    border-bottom: 1px solid #ececec;
}
.comment-list__pagination-bottom {
    border-top: 1px solid #ececec;
}
.comment-list .comments__item-wrap {
    border-top: 1px solid #ececec;
}
.comment-list .comments .media-block__wrap>div>div {
    border-top: 1px solid #ececec;
}
.comment-list .comments__item {
    border-top: 1px solid #ececec;
}
.panel, .fieldset, .vBulletin_editor, .button, [id=smiliebox] {
    background: #fff !important;
    color: #a7a7a7;
    font-weight: unset;
    border: 1px solid #dedede !important;;
}
.heading__sub-info ._item {
    color: #201f25;
}
.header__notification {
    margin: 13px 0 0 0;
}
strong {
	font-weight: normal;
}
.nearposts-list {
    display: none;
}
.content__sidebar ul li:hover {
    background: #fff;
}
.boxes__menu-top ul li a:hover {
    background-color: #f7f7f7;
}
.boxes__menu-top ul li a ._icon {
	display: none;
}
.box-list-title a:hover {
    background-color: #f7f7f7;
}
}
