css:
@-moz-document domain("learn.co") {
  body {
    font-family: "BlinkMacSystemFont", "Lucida Grande", "Segoe UI", Ubuntu,
      Cantarell, sans-serif;
    background-color: #282c34;
    color: #abb2bf;
  }

  a {
    color: rgb(86, 182, 194);
    text-decoration: none;
  }

  a:hover {
    color: #00bce1 !important;
  }

  header.site-header {
    background-color: #3b4048;
    border-bottom: 1px solid rgb(24, 26, 31);
  }

  .site-subheader {
    background-color: #3b4048;
    border-bottom: 1px solid rgb(24, 26, 31);
  }

  .heading--color-green,
  .site-header__karma__points {
    color: rgb(152, 195, 121);
  }

  .menu {
    background-color: #3b4048;
    border: solid 1px rgb(24, 26, 31);
  }

  .site-nav__item__container:hover {
    background-color: #292d35;
  }

  .heading--color-blue,
  .menu__list__item a:hover,
  .site-nav__item__container:hover {
    color: rgb(97, 175, 239);
  }

  .site-nav__item[data-active="true"].site-nav__item--leaf
    > .site-nav__item__container {
    font-weight: bold;
    background-color: #cfcfcf;
    color: rgb(97, 175, 239);
  }

  .heading--color-black,
  .site-nav__item[data-active="true"]:not(.site-nav__item--leaf)
    > .site-nav__item__container {
    color: #abb2bf;
  }

  .site-nav__item__status[data-completion="complete"] {
    color: rgb(152, 195, 121);
  }

  .menu__head {
    border-bottom: 1px solid rgb(24, 26, 31);
  }

  .menu__head,
  .menu__foot {
    background-color: #3b4048;
  }

  .menu--side-alt {
    background-color: #3b4048;
  }

  .module--island {
    border: 0px solid rgb(24, 26, 31);
    background-color: #3b4048;
  }

  section.site-sidebar {
    background-color: #3b4048;
    border-left: 1px solid rgb(24, 26, 31);
  }

  .site-sidebar__actions {
    border-bottom: 1px solid rgb(24, 26, 31);
  }

  .button--color-purple:hover,
  .button-badge-unit[data-status="active"] .button-badge-unit__button:hover {
    background-color: rgb(220, 157, 40);
  }

  .button--color-purple,
  .button-badge-unit[data-status="active"] .button-badge-unit__button {
    background-color: rgb(229, 192, 123);
  }

  .status-alert[data-status="good"] .status-alert__title,
  .status-alert[data-status="complete"] .status-alert__title,
  .status-alert[data-status="online"] .status-alert__title {
    color: rgb(152, 195, 121);
  }

  .status-alert[data-status="good"] .status-alert__bubble,
  .status-alert[data-status="complete"] .status-alert__bubble,
  .status-alert[data-status="online"] .status-alert__bubble {
    border-color: rgb(152, 195, 121);
  }

  .button--color-blue,
  .text-block--readme .quiz-block .assessment-btn,
  .status-alert__button,
  .status-alert[data-status="good"] .status-alert__button--main,
  .status-alert[data-status="complete"] .status-alert__button--main,
  .status-alert[data-status="online"] .status-alert__button--main,
  .status-alert[data-status="pending"] .status-alert__button--main,
  .module--tabular > .module__head .module__head__tab.active .button,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .text-block--readme
    .quiz-block
    .assessment-btn,
  .text-block--readme
    .quiz-block
    .module--tabular
    > .module__head
    .module__head__tab.active
    .assessment-btn,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .status-alert__button--main,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .button-input-unit__button,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .button-badge-unit__button {
    background-color: rgb(97, 175, 239);
  }

  .button--color-blue:hover,
  .text-block--readme .quiz-block .assessment-btn:hover,
  .status-alert__button:hover,
  .status-alert[data-status="good"] .status-alert__button--main:hover,
  .status-alert[data-status="complete"] .status-alert__button--main:hover,
  .status-alert[data-status="online"] .status-alert__button--main:hover,
  .status-alert[data-status="pending"] .status-alert__button--main:hover,
  .module--tabular > .module__head .module__head__tab.active .button:hover,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .text-block--readme
    .quiz-block
    .assessment-btn:hover,
  .text-block--readme
    .quiz-block
    .module--tabular
    > .module__head
    .module__head__tab.active
    .assessment-btn:hover,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .status-alert__button--main:hover,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .button-input-unit__button:hover,
  .module--tabular
    > .module__head
    .module__head__tab.active
    .button-badge-unit__button:hover {
    background-color: rgb(61, 153, 228);
  }

  .button--color-green,
  .status-alert[data-status="good"] .status-alert__button,
  .status-alert[data-status="complete"] .status-alert__button,
  .status-alert[data-status="online"] .status-alert__button,
  .module--cloud .module--cloud__button--main {
    background-color: rgb(152, 195, 121);
  }

  .button--color-green:hover,
  .status-alert[data-status="good"] .status-alert__button:hover,
  .status-alert[data-status="complete"] .status-alert__button:hover,
  .status-alert[data-status="online"] .status-alert__button:hover,
  .module--cloud .module--cloud__button--main:hover {
    background-color: #75bd4a;
  }

  .module--lilypad .module__head {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
  }

  .module--lilypad .module__body {
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    margin-top: 0;
    margin-bottom: 0;
    padding: 12px 16px;
  }

  .status-alert__title,
  .status-alert__subtitle {
    color: white;
  }

  .module--cloud .module__body,
  .module--cloud .module__foot {
    padding: 32px 64px;
    background-color: #3b4048;
  }

  .site-main__inner__footer {
    background-color: #3b4048;
  }

  .text-block--readme ol li:before {
    background-color: rgb(33, 37, 43);
  }

  .heading code,
  .bubble-bar__label code,
  .button code,
  .text-block--readme .quiz-block .assessment-btn code,
  .status-alert__button--main code,
  .button-input-unit__button code,
  .button-badge-unit__button code,
  .text-block h1 code,
  .text-block h2 code,
  .text-block h3 code,
  .text-block h4 code,
  .text-block h5 code,
  .text-block h6 code,
  .menu__list__item a code,
  .table-grid--default .table-grid__row--header code,
  .site-header__nav-item__heading code,
  .site-header__karma__label code,
  .site-header__karma__points code,
  .site-main__inner__footer code,
  .site-nav__item__container code {
    background-color: rgb(55, 61, 71);
    border-radius: 4px;
    padding: 0 8px 4px;
  }

  .text-block--readme code {
    background-color: rgb(55, 61, 71);
  }

  .text-block--readme pre {
    border-top: 6px solid rgb(152, 195, 121);
    background-color: rgb(33, 37, 43);
  }

  .text-block--readme .prettyprint.linenums ol li:before {
    font-weight: bold;
    color: rgb(55, 61, 71);
  }

  .highlight .k {
    color: rgb(198, 120, 221);
  }

  .highlight .nc {
    color: rgb(229, 192, 123);
  }

  .highlight .nf {
    color: rgb(97, 175, 239);
    font-weight: bold;
  }

  .highlight .k,
  .highlight .kv {
    color: rgb(198, 120, 221);
    font-weight: bold;
  }

  .highlight .kp {
    color: rgb(97, 175, 239);
  }

  .highlight .ss {
    color: rgb(86, 182, 194);
    font-weight: bold;
  }

  .highlight .o {
    color: #abb2bf;
  }

  .highlight .na {
    color: #d19a66;
  }

  .highlight .nt {
    color: #e06c75;
  }

  .highlight .nb {
    color: #60afef;
  }

  .highlight .no {
    color: rgb(229, 192, 123);
  }

  .highlight .s {
    color: #97c378;
  }

  .highlight .sh {
    color: rgb(152, 195, 121);
  }

  .highlight .s1 {
    color: #97c378;
  }

  .highlight .s2 {
    color: rgb(152, 195, 121);
  }

  .highlight .mi {
    color: rgb(209, 154, 102);
  }

  .highlight .c1 {
    color: rgb(92, 99, 110);
    font-style: italic;
  }

  .text-block h3 code {
    background-color: rgb(55, 61, 71);
  }

  .text-block--readme blockquote {
    background-color: #22262c;
    border-top: 8px solid #3b4047;
    padding: 20px 32px;
  }

  .button--color-grey-faint,
  .status-alert[data-status="neutral"] .status-alert__button {
    background-color: #292d35;
  }

  .button--color-grey-faint:hover,
  .status-alert[data-status="neutral"] .status-alert__button:hover {
    background-color: #22262c;
  }

  .button--icon-only .button__icon {
    fill: #c4c4c4;
  }

  .button--icon-only:hover .button__icon {
    fill: #00bce1;
  }

  .site-header__user__menu__toggle {
    background-color: #292d35;
  }

  .site-header__user__menu__toggle:hover {
    background-color: #22262c;
  }

  .menu__list__item a:hover {
    background-color: #292d35;
  }

  .site-main {
    background-color: #252830;
  }

  .module__head {
    border-bottom: 1px solid #f9c51a !important;
  }

  ol.list--separators-grey-faint > li + li,
  ul.list--separators-grey-faint > li + li {
    border-top: 1px solid #f9c51a !important;
  }

  .module--head-nowrap .module__head:after {
    background: none;
  }

  .site-sidebar__actions,
  .list.list--separators-grey-faint.list--spacing-large.util--padding-tl.util--padding-bl {
    background: #3b3f48;
  }

  .inline-svg--velocity__ticks__tick {
    stroke: #fff;
  }

  a:hover {
    color: #00bce1 !important;
  }

  .media-block__content > * {
    color: #aaaaaa;
  }

  .stat--block-chart__main__inner__chart__block__inner {
    background-color: #aaaaaa;
  }
}
