css:
/**********************************/
/* The Club v1.0 skin  */
/* Date: 2/04/2009               */
/* Made by: Maek              */
/**********************************/

@-moz-document domain("hattrick.org") {


.boxLeft

{
	background: #fae822 !important;
}


#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
}
#footer a
{
        color: #ad1b3f !important;
        font-weight: bold !important;
}
#ticker
{
	position: absolute !important;
	top: 20px !important;
	left: 25px !important;
font-weight: bold !important;
text-decoration: blink   !important;


}




#ticker div a
{
	padding-bottom: 2px !important;
	color: black !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 25px !important;
	margin-left:0px !important;
}
#teamLinks
{
        color: #ffffff !important;
        margin-top: 15px !important;
	margin-left: 7px !important;
}
div#teamLinks a
{
        color: black !important;
}
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: WHITE !important;}
div[id="time"] {color: WHITE !important;}

/*--Page background-----------------------------------------------*/
body {background: black !important;}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #ad1b3f !important;
        border-bottom: solid thin #ad1b3f !important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: #fae822 !important;  
        background: black !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #ad1b3f !important; 
        background: none !important;
        border-bottom: solid 1px #ad1b3f !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/
/*Left menu*/
.subMenuBox .boxHead
{
        color: #fae822 !important;  
	background: black !important;

}
.subMenuBox .boxHead .boxLeft
{
        color: #fae822 !important; 
        background: black !important;

}
/*Centre box*/
.main .boxHead h2 
{
        color: #fae822 !important; 
	background: black !important;

}
/*Right menu*/
.sidebarBox h2 
{
        color: #fae822 !important; 
	background: black !important;

}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	border-bottom: solid 1px #ad1b3f !important;
	color: #ad1b3f !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;

}
/*Central header paths*/
h2 a 
{
        color: #fae822 !important;
 text-decoration: none !important;
}
h2 a:hover 
{
        color: #ad1b3f !important;  
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #ad1b3f !important;
        border-bottom: solid thin #ad1b3f !important;
}
/*General Links*/
a
{
        color: #ad1b3f !important;
	text-decoration: none !important;
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
        text-decoration: none !important;
	color: black !important;
}


.subMenuBox:after

{ content: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/pokal-0025.gif)
 !important

   
}





.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}


.sidebarBox:after

{ content: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/pokal-0025.gif)
 !important
}






/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
        background-image: none !important;
        background-color: #fae822 !important;   
}
/*Image header (banner)*/
#header 
{
        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/ende_T_60.gif) !important;
	height: 102px !important;
	padding-top: 40px !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 20px !important;
	margin-top: 5px !important;
        background: black !important;
text-decoration: none !important;
}
#menu a
{
	line-height: 20px !important;  
        color: #fae822 !important;
text-decoration: none !important;
}
div#menu a:hover 
{
        color: black !important; 
        background: #fae822 !important; 
}
a:hover
{
	color: #ad1b3f !important; 
text-decoration:none !important; 
}
/*bottom white line*/
div[id="bottom"]
{
        background: #fae822 !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"]  
a:hover {color: #ad1b3f !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: #FAE822 
 !important;}
div[class="message"] a {color: #de4747 !important;}
div[class="signature"] a {color: #de4747 !important;}

#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #FAE822 


}
.cfWrapper
{
	border: solid 1px #c70000 !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: #fae822 !important; 
}
.cfUserInfo a
{
        color: #ad1b3f !important;
}
.folderHeader a:link 
{
	color: #de4747 !important;
	font-weight: bold !important; 

}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background:  #FAE822 
!important; 
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/


/*shortcuts*/
img.scNewMail 
{

}
}
