css:
@-moz-document domain("volafile.org"), domain("volafile.io") {
html {
    --main-bg-color: #333333;
    --snd-bg-color: #101010;
    --input-color: #131313;
    --input-disabled-color: #8e8e8e;
    --snd-input-color: #333333;
    --box-color: #282828;
    --slider-color: #707070;
    --hi-color: #282828;
    --hi-hover-color: #505050;
    --file-list-color: #282828;
    --file-color: #3a3a3a;
    --file-hover-color: #505050;
    --header-hi-color: #404040;
    --tag-color: rgba(160, 150, 150, .3);
    --button-color: #dcdcdc;
    --button-hi-color: #ececec;
    --button-light-color: #666666;
    --button-light-hi-color: #777777;
    --channel-color: #d880fc;
    --context-bg-color: #444444;
}

*, *:after {
    border-color: var(--main-bg-color) !important;
}

html {
    background-color: var(--snd-bg-color);
}

select {
    background-color: #424242;
}

table th {
    background-color: var(--main-bg-color);
}

#chat_frame,
#files_frame {
    border-top-color: var(--header-hi-color) !important;
}

#admin_button,
#chat_header,
#files_header,
#name_container,
#room_search {
    height: 2em;
    line-height: 2em;
}

#admin_button {
    background-color: var(--main-bg-color);
    width: 2em;
}

#admin_button_icon {
    font-size: 1em;
    vertical-align: center;
}

#name_container {
    padding-left: 0.5ex;
}

#clearsearch,
.header_row_element {
    line-height: 2em;
}    

#home_button {
    height: 2em;
    width: 2em;
    padding: 0 .8em;
}

.volafile_icon_svg.button .v_stroke {
    stroke-width: 80px;
}

#chat_frame,
#files_frame {
    top: 2em;
}

#room_name {
    font-size: 1.5em;
}

#rename_container {
    line-height: 1.7em;
}

#rename_input {
    font-size: 1em;
    width: 20em;
}

.filelist_file {
    border-bottom: 1px solid var(--main-bg-color);
}

#file_list.even .filelist_file:nth-child(2n),
#file_list.uneven .filelist_file:nth-child(2n+1) {
    background-color: var(--file-list-color);
}

.file_selected {
    background-color: #464646 !important;
}

.filelist_file:hover:before {
    border-color: var(--file-hover-color);
}

.scroller-slider,
.nano>.pane>.slider {
    background-color: var(--slider-color);
}

#chat_hbar_container {
    box-shadow: 0 -5px 5px 0 var(--snd-bg-color) !important;
}

#file_notifier,
#filter_reminder,
#chat_hbar {
    background-color: var(--main-bg-color);
}

#file_notifier:hover,
#filter_reminder:hover {
    background-color: var(--hi-hover-color);
}

#chat_hbar {
    display: flex;
    justify-content: flex-end;
    align-items: flex-end;
    border: 0;
}

#chat_name {
    width: 16ex;
    margin: 0;
    height: auto;
    border: 0;
    margin-top: 1px;
    margin-bottom: 1px;
    padding-left: 1ex;
    padding-right: 1ex;
}

#user_count {
    margin-left: 1ex;
    margin-right: 1ex;
    min-width: 5ex;
}

#chat_input {
    padding: 0.5ex 1ex;
    font-size: inherit;
}

#toggles span.toggle_icon {
    color: var(--input-disabled-color) !important;	
}

#toggles>.toggle.enabled span.toggle_icon {
    color: #f6f1f1 !important;    
}

#search_input:hover,
#toggles>.toggle:hover {
    background-color: rgba(120, 120, 120, 0.1);
    border-radius: 3px;
}

body:not(.mobile_filelist) #toggles span.toggle_container {
    margin-right: 1ex;
}

#toggles>.toggle:active {
    background: transparent;
}

#room_search {
   background-color: var(--input-color);
}

body:not(.mobile_filelist) #room_search {
   margin-left: 1em;
}

#search_input:active, #search_input:focus, #search_input:valid {
    background-color: transparent;
    width: 25em;
}

.ui_frame_contextmenu {
    border-radius: 0;
    border: 0;
}

.ui_frame_contextmenu,
.ui_frame_contextmenu .button.light,
.ui_frame_contextmenu .button.light:active,
.ui_frame_contextmenu .button.light:visited {
    background-color: var(--context-bg-color);
}

input[type=text],
input[type=password],
textarea {
    background-color: var(--input-color);
}

input[type=text]::-webkit-input-placeholder,
input[type=password]::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
    color: var(--input-disabled-color);
    opacity: 1;
}

input[type=text]::placeholder,
input[type=password]::placeholder,
textarea::placeholder {
    color: var(--input-disabled-color);
    opacity: 1;
}

input[type=text][disabled],
input[type=password][disabled],
textarea[disabled] {
    color: var(--input-disabled-color);
}

input[type=radio]:disabled+span._icon:before,
input[type=checkbox]:disabled+span._icon:before,
.radio_toggle {
    color: var(--input-disabled-color);
}

.main > form input[type=text],
.main > form input[type=password] {
    background-color: var(--snd-input-color);
}

input.defaultValue[type="text"],
input[type="text"][disabled],
input.defaultValue[type="password"],
input[type="password"][disabled],
textarea.defaultValue,
textarea[disabled] {
    color: #aaaaaa;
}

.chat_message > i {
    color: var(--channel-color);
}

a.chat_room {
    text-decoration: underline;
    display: inline-block;
}

a.chat_room:after {
    content: none;
    display: none;
}

a.chat_room:before {
    display: inline-block;
    content: '#';
    font-weight: bold;
    color: rgba(255,255,255,0.4);
    padding-right: 0.3ex;
    text-decoration: none !important;
}

a.chat_room:hover:before,
a.chat_room:active:before{
     color: rgba(255,255,255,0.8);
}

.chat_file {
    background-color: var(--file-color);
}

.chat_file:hover,
.chat_file:active {
    background-color: var(--file-hover-color);
}

#chat_messages_frame:before {
    border-left-color: var(--snd-bg-color);
}

.chat_message.highlight {
    border-left-color: var(--hi-color) !important;
    background-color: var(--hi-color);
}

.ui_frame,
#overlay {
    background-color: var(--box-color);
}

.dropdown:hover,
a.header_row_element:hover {
    background-color: var(--header-hi-color);
}

a.header_row_element:active {
    background-color: var(--header-hi-color);
}

.header_row_element.clickable:hover,
a.header_row_element_disabled,
a.header_row_element_disabled:active,
a.header_row_element_disabled:hover,
a.header_row_element_disabled:visited {
    background-color: var(--header-hi-color);
}

.header_row_element_disabled.clickable,
.header_row_element_disabled.clickable:hover {
    color: var(--input-disabled-color);
}

.dropdown_item:hover {
    background-color: var(--hi-hover-color);
}
.dropdown_item:active {
    background-color: var(--header-hi-color);
}
.dropdown_list {
    background-color: var(--header-hi-color);
}
.dropdown:after {
    border-color: var(--header-hi-color);
}

.file_status:before,
hr {
    border: 1px solid #434343;
}

.file_tag {
    background-color: var(--tag-color) !important;
}
.button:not(.gallery_button),
input[type=submit] {
    background-color: var(--button-color);
}

.button:visited:not(.gallery_button),
input[type=submit]:visited {
    background-color: var(--button-color);
}

.button:hover:not(.gallery_button),
input[type=submit]:hover {
    background-color: var(--button-hi-color);
}

.button:active:not(.gallery_button),
input[type=submit]:active {
    background-color: var(--button-hi-color);
}

.gallery_button {
    font-size: x-small;
}

.gallery_area {
    border: 0;
}

.button.light,
.button.light:visited,
input[type=submit].light,
input[type=submit].light:visited {
    background-color: var(--button-light-color);
}
.button.light:hover,
input[type=submit].light:hover {
    background-color: var(--button-light-hi-color);
}

#name_container {
    background-color: var(--main-bg-color);
}
.ui_frame_content .button.light:hover {
    background-color: var(--button-light-hi-color);
}

#chat_header,
#files_header {
    background-color: var(--main-bg-color);
}

#chat_name_container {
    background-color: var(--main-bg-color);
}

#home_button {
    background-color: var(--main-bg-color);
}

#loading_frame {
    background-color: var(--main-bg-color);
}

#loading_text {
    color: white;
}

#room_name_angle_down,
#chat_user_angle_up,
#chat_user_icon,
#clearsearch {
    opacity: 0.3;
}

#chat_user_angle_up {
    padding-left: 0.7ex;
    display: none;
}

#chat_user_icon {
    padding-right: 0.7ex;
}

#toggles span.toggle_text {
    display: none;
}

#chat_name_container {
    order: 1;
}

#chat_notifier {
    order: 2;
}

#chat_status {
    order: 3;
}


#gallery_title {
    font-size: small;
}

#main_small_links {
    background-color: #434343;
}

.top_nav_item,
.top_nav_item:hover,
.top_nav_item:visited,
body {
    color: rgb(210, 203, 133);
}

.top_nav_item:hover {
    background: var(--header-hi-color);
}

.chat_message .username .clickable:not(:hover) {
    color: inherit;
}

.chat_message .username {
    color: rgb(170, 170, 180);
    float: none;
}

.chat_message.user .username {
    color: #23d16f;
}

.chat_message.donator .username {
    color: #42ff96;
}

.chat_message.staff .username {
    color: #fc807e;
}

.chat_message.staff:not(.user) .username {
    color: #ff6c00;
}

.chat_message.admin .username {
    color: #d880fc;
}

.icon-image:before {
    color: lightskyblue;
}

.icon-film:before {
    color: yellowgreen;
}

.icon-book:before {
    color: lightsalmon;
}

.icon-archive:before {
    color: bisque;
}

.icon-music:before {
    color: orchid;
}

.chat_room_url {
    background-color: var(--hi-hover-color);
}

a.header_row_element_disabled,
a.header_row_element_disabled:active,
a.header_row_element_disabled:hover,
a.header_row_element_disabled:visited {
    color: #e0e0e0;
}

#radio_container {
    background-color: var(--main-bg-color);
}

#radio_current {
    background-color: #555555;
}

#radio_progress {
    background-color: #5e5e5e;
}

#radio_volume_bar,
#radio_load_progress {
    background-color: #535353;
}

#radio_volume_slider,
#radio_play_progress {
    background-color: #777777;
}

.file_right_part {
    text-align: right;
}

body:not(.mobile_filelist) .file_right_part {
    min-width: 166px;
}

body:not(.mobile_filelist) .file_status {
    min-width: 98px;
}

.file_buttons:before, .file_status:before {
	border-color: var(--main-bg-color) !important;
}

#uploadButton {
    font-size: 18px;
    padding: 0 .5em 1px .5em;
}

#clearsearch {
    padding-left: 0.6ex;
}

#call_to_action,
#call_to_action_container.active #call_to_action {
    color: #434343;
}
.drag_here_svg .drag_here_rect,
#call_to_action_container.active .drag_here_rect {
    stroke: #434343;
}

#call_to_action_container:before {
    background-color: var(--snd-bg-color);
}

.page_admin_reports table,
.page_admin_modlog table {
    table-layout: fixed;
    width: 100%;
}
.page_admin_modlog table th:nth-child(1) {
    max-width: 15%;
}
.page_admin_modlog table th:nth-child(3) {
    width: 56%;
}
.page_admin_modlog table th:nth-child(4) {
    width: 7ex;
}

.page_admin_reports table th:nth-child(1) {
    max-width: 9%;
}
.page_admin_reports table th:nth-child(4) {
    width: 10%;
}
.page_admin_reports table th:nth-child(3) {
    width: 53%;
}

#bottom_navigation_content {
    background-color: var(--main-bg-color);
}
html,body {
	font-family: consolas, 'haxrcorp', consolas, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 100 !important; font-size: 16px !important;}

b,strong {
	font-family: monospace, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 200 !important; }
h1,h2,h3,h4,h5,h6 {
	font-family: 'lain',monospace, lucida console, lucida sans typewriter, monaco !important; 
	font-weight: 400; }
@font-face {
  font-family: 'lain';
  src: url('https://lainchan.org/stylesheets/fonts/nrdyyh.woff') format('woff'),
       url('https://lainchan.org/stylesheets/fonts/fonts/tojcxo.TTF') format('truetype');
}
#chat_messages_frame {
    font-family: monospace, lucida console, lucida sans typewriter, monaco !important;
}
.username {
    font-family: monospace, lucida console, lucida sans typewriter, monaco !important;
}
.file_tag.tag_key_user {
    background-color: rgba(35, 209, 111, .3) !important;
    border-radius:2px;
}
.file_tag {
	font-size:.65em;
}
}
