css:
@-moz-document url-prefix("https://tasks.gtaskd.com/") {
:root {

--color-background: #191919;  
--color-button: #353535;
--color-text-messages: #444444;
--color-text-control: #A4A4A4;
--color-text-list: #EEEEEE;
--color-text-title: #A1BADF;
--color-borders: #A4A4A4;
--color-id: chartreuse;
  
}

/* main element colors */
div, body {
  background-color: var(--color-background) !important;
  color:  var(--color-text-list) !important;
}

/* hide logo */
#gtaskd-logo {
  display: none !important;
}

/* task list title color */
#tasklistTitle {
  color:  var(--color-text-title) !important;
}

/* hide synchronization messages */
#api_status {
  color:  var(--color-text-messages) !important;
  display: none;
}

/* task list color */
.ui-menu-item-wrapper {
  color:  var(--color-text-control) !important;
}

/* disable hover effects (border) */
.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, a.ui-button:active, .ui-button:active, .ui-button.ui-state-active:hover {
  border: none;
}

/* disable hover effects (margin shift) */
.ui-menu .ui-state-focus, .ui-menu .ui-state-active {
  margin: 0px;
}

/* hide control buttons */
#gtaskd-controls {
  display: none !important;
}

/* reduce height of header to account for missing control buttons */
#gtaskd-header {
  height: 0px !important;
}

/* move task list up as well and fill remaining space */
#gtaskd-tasklists {
  top: 30px;
  height: calc( 100% - 30px );
}

/* scrollbar width */
::-webkit-scrollbar {
  width: 10px;
}

/* scrollbar track color */
::-webkit-scrollbar-track {
  background: var(--color-background); 
}
 
/* scrollbar handle color */
::-webkit-scrollbar-thumb {
  background: var(--color-button); 
}

/* fix 1 pixel cutoff on top of 1st task list item */
.ui-menu {
  margin-top: 1px;
}

/* disable outline on focused list item */
.actualContent:focus{
  outline: none;
}

/* get rid of yellow background when dragging to re-arrange list items */
.ui-state-highlight {
  background: var(--color-background);
}

/* loading progress spinner */
.gtaskd-spinner div:after {
  background: var(--color-text-title);
}

/* lighten unckecked circles */
.gtaskd-task-checkbox {
  background-image: url('data:image/png;base64,');
}

/* done checkmark */
.gtaskd-status-completed > .gtaskd-task-checkbox {
    background-image: url('data:image/png;base64,');
}
}
