@-moz-document domain("ohnotheydidnt.livejournal.com"), domain("omonatheydidnt.livejournal.com") {
.b-singlepost.hentry.h-entry.js-emojis.b-singlepost-mobileadaptive.b-singlepost-withbodybanner.ng-scope {
    width: 1000px;
    margin: auto;
    text-align: justify;
    background:#ffffff;
    border-width:0 20px;
    border-style:solid;
    border-color:#eeeeee;
}
.b-leaf-header {
    background: #f9f9f9!important;
    padding: 0px;
}
.b-leaf-article {
    padding: 0 10px;
}
.b-leaf-footer {
    padding: 0 10px;
}
.b-leaf-collapsed .b-leaf-details {
    padding: 0 10px;
}




.twitter-tweet,
.instagram-media{
    margin: auto!important;
}
}

@-moz-document domain("ohnotheydidnt.livejournal.com") {
.s-body{
    background: #7c578c;
}
.b-leaf-inner {
    border-left: 10px solid #7c578c;
}
.j-l-wrapper {
    background: #7c578c!important;
}
}

@-moz-document domain("omonatheydidnt.livejournal.com") {
.s-body {
    background: #b3527d;

}
.b-leaf-inner {
    border-left: 10px solid #b3527d;
}
}
