css:
@-moz-document url-prefix("https://qiita.com/") {
body,
.p-home,
#globalFooter
{
    color: #bbb;
    background-color: #222;
}
.p-home_main {
    background-color: #272822;
}
.p-home .an-Item,
.p-home .qzf-ItemList,
.p-home .ra-UserList,
.p-home .ra-TagList,
.p-home .ra-OrganizationList,
.p-home .ra-OrganizationList a,
.p-home .ra-SponserList {
    color: #bbb;
    background-color: #272822;
}
a {
    color: #66d9ef;
}
a:hover {
    color: #82ecff;
}
.p-home_menuItem:hover {
    color: #333;
}
/* popup */
.uh-Container
{
    color: #bbb;
    background-color: #353535;
}
.uh-Header_profile-username,
.uh-Footer_counter-count,
.uh-Footer_counter-count
{
    color: #bbb;
}

/*個人ユーザページ*/
.userActivityChart_svg.js-userActivityChartSVG.c3,
.userActivityChart_stat,
ul.nav.nav-tabs li
{
    background: #777;
}
article.media.ItemLink,
.newUserPageProfile_info,
.st-Header_dropdown.is-open
{
    background-color: #333;
}
ul.ItemLink__status li,
i.fa.fa-github-square,
.newUserPageProfile_info_body,
.newUserPageProfile_info_heading,
.ItemLink__info,
.tableList_item
{
    color: #bbb;
}
/* menu pop */
.st-Header_dropdownItem:hover
{
    background-color: #444;
}
.st-Header_dropdownItem
{
    color: #bbb;
}

.uh-Container,
.is-searchform-open .st-Header_searchModalInput
{
    color: #bbb;
    background-color: #353535;
}

}

@-moz-document regexp("https://qiita.com/.+/items/.+") {
.p-items_wrapper,
.commentForm_contentHeader,
.commentForm_body,
.commentForm_imageUploader
{
    color: #bbb;
    background-color: #222;
}
.p-items_article,
.p-items_wrapper .p-items_article {
    color: #bbb;
    background-color: #333;
/*     background-color: #555; */
}
img.emoji  {
    background-color: #333;
}
h1, h2, h3, h4, h5, h6,
li, p, dt,
.it-Header_title,
#item-8e2074d4e70339a1ccb5 p,
#logly-lift-4279494 .logly-lift-ad-title,
.it-MdContent
{
    color: #bbb;
}
.tocTree_contents .active>a, .tocTree_contents a:hover,
.commentForm .preview-area {
    color: #bbb;
    background-color: #444;
}
.dropdown-menu {
    background-color: #414248;
}
.dropdown-menu>li>a {
    color: #aaa;
}
.comment_content {
    color: #bbb;
    background-color: #444;
}
a.it-Header_authorName {
    color: #337ab7;
}
.it-MdContent th {
    background-color: #222;
    color: #bbb;
}
table tr:nth-child(odd) {
    color: #222;
}
table tr:nth-child(even) {
    color: #bbb;
}
img {
    background-color: #ccc;
}
}

@-moz-document url-prefix("https://qiita.com/stock") {
.ItemLink {
    background-color: #272822;
}
.ItemLink__info,
ul.ItemLink__status,
.SidePanelItemMenu__count,
.SidePanelItem__header,
.SidePanelItemMenu__more.p-button.p-button--m {
    color: #777;
}
}

@-moz-document url-prefix("https://qiita.com/tags/"), url-prefix("https://qiita.com/organizations/") {
.p-tagShow,
#globalFooter {
    color: #bbb;
    background-color: #222;
}
.p-home .ra-SponserList,
.p-tagShow .tsi-Content,
section.tst-ArticleList,
.p-tagShow_mainBottom .tsf-ArticleList,
.p-tagShow .tsr-UserList,
.p-tagShow .tsr-OrganizationList
{
    color: #bbb;
    background-color: #333;
}
.tst-ArticleBody_title,
.tsf-ArticleBody_title,
a.st-Pager_link
{
    color: #bbb;
}

button.tsi-Content_followButton.tsi-Content_followButton-follow {
    color: #bbb;
    background-color: #444;
}
button.tsi-Content_followButton.tsi-Content_followButton-follow:hover {
    color: #bbb;
    background-color: #666;
}

}

@-moz-document url-prefix("https://qiita.com/organizations/") {
.allWrapper
{
    color: #bbb;
    background-color: #222;
}
.p-organization_sidebar .op-SideCard,
.od-Content
{
    color: #bbb;
    background-color: #333;
}
h1.op-About_name,
p.op-About_description,
dd.op-CounterItem_count,

span.ot-TagItem_name,
.ot-TagItem_count span,
a.of-ItemLink_header-title
{
    color: #bbb;
}

}

@-moz-document url-prefix("https://qiita.com/users") {
.hovercard,
ul.pagination li a
{
    color: #bbb;
    background-color: #333;
}
}
