css:
@-moz-document domain("exsite24.pl") {
html {
    background-color: #1d1d1d;
    color: #bbb;
}
.boxed {
    background: #353535;
    border: 1px solid #545454;
}
.featured ul a {
    color: #b9b9b9;
}
.content .movie h3, .content .movie h3 a {
    color: #bbb7b7;
}
.featured li:hover {
    background: #4e4e4e;
}
.featured li {
    border-top: 1px solid #676767;
}
.quote, .scriptcode {
    border-left: 3px solid #ff6600;
    border-top: 1px solid #545454;
    border-right: 1px solid #545454;
    border-bottom: 1px solid #545454;
    background: #2b2b2b;
    padding: 20px 10px;
    margin: 20px 0px 0px 0px;
    color: #bbb7b7;
}
a {
    color: #bbb7b7;
}
.movie .informacje {
    border-bottom: 1px solid #545454;
}
.informacje span {
    border-left: 1px solid #545454;
}
.featured h3, .boxed h3 {
    border-bottom: 1px solid #545454;
}
nav.boxed section {
    border-top: 1px solid #545454;
}
nav.menu ul ul a {
    color: #171717;
}
nav.menu ul a {
    color: #d8d8d8;
}
nav.menu ul ul li, nav.menu ul ul li:hover {
    filter: invert(79%);
}
.pagination .last {
    border-right: 1px solid #545454;
}
.pagination .first {
    border-left: 1px solid #545454;
    color: #b7b9bb;
}
.pagination li {
    background: #353535;
    border-top: 1px solid #545454;
    border-bottom: 1px solid #545454;
}
.pagination .prev, .pagination .next {
    border: 1px solid #545454;
}
.pagination li.prev {
    color: #b7b9bb;
}
.onoff, .offon {
    filter: invert(79%);
}
footer {
    border-top: #545454 solid 1px;
}
}
