css:
@-moz-document domain("breitbart.com"), domain("www.breitbar.com") {
body {
    background: #1b1b1b;
    background-color: rgb(27, 27, 27);
}

.post-tmpl-default, single, single-post, pid-10353990, tf-single, pt-post, c-tech {
    background-color: #1b1b1b;
}

#HaDW {
    background-color: #1b1b1b;
}

.a8d, padok {
    background-color: #1b1b1b;
}

#HWT a.logo, #HWT a.logo:hover {
    color: #c0c0c0;
}

#ContainerW, #MainW {
    background: #333;
    background-color: rgb(51, 51, 51);
}

#HWT a {
    color: #c0c0c0;
    border-right-color: #1b1b1b;
    border-left-color: #1b1b1b;
}

.the-article, post-10353990, post, type-post, has-post-thumbnail, category-border, category-latin-america, category-tech {
    color: #c0c0c0;
    border-right: 1px solid #333;
    border-left: 1px solid #333;
    border-right-color: #333;
    border-left-color: #333;
}

article h2 {
    color: #c0c0c0;
}

/*background below share buttons under article title*/
#bbvb {
    position: relative;
    border-bottom: 5px solid #333;
    border-bottom-color: rgb(51, 51, 51);
}
/*Space between share buttons*/
#bbvb>a {
    border-right: 9px solid #333;
    border-right-color: rgb(51, 51, 51);
}

/*LISTEN TO STORY*/
#PollyC b:after {
    color: #c0c0c0;
}
/*Headphones icon*/
#PollyC b:before {
    color: #c0c0c0;
}
#PollyC {
    border-top: 2px solid #c0c0c0;
    border-top-color: rgb(192, 192, 192);
    border-bottom: 1px solid #c0c0c0;
    border-bottom-color: rgb(192, 192, 192);
}

/*Borders above and below share buttons under article*/
#bbsh {
    border-bottom: 15px solid #333;
    border-bottom-color: rgb(51, 51, 51);
    border-top: 10px solid #333;
    border-top-color: rgb(51, 51, 51);
}
/*Right border of tweet button below article*/
#bbsh .addthis_button_twitter {
    border-right: 9px solid #333;
    border-right-color: rgb(51, 51, 51);
}

#SideW {
    background: #333333;
    background-color: rgb(51, 51, 51);
}

#HPSW a {
    color: #c0c0c0;
}

element.style {
    background-color: rgb(27, 27, 27);
}

#FootW {
    background: #1b1b1b;
    background-color: rgb(27, 27, 27);
}
#FootW #FWI {
    border-top: 1px solid #2a2e2e;
    border-top-color: rgb(42, 46, 46);
    border-bottom: 1px solid #2a2e2e;
    border-bottom-color: rgb(42, 46, 46);
}
#NavF {
    border-left: 1px solid rgb(42, 46, 46);
    border-left-color: rgb(42, 46, 46);
    border-right: 1px solid rgb(42, 46, 46);
    border-right-color: rgb(42, 46, 46);
}
#FootW #FWI p a {
    background: #101010;
    background-color: rgb(16, 16, 16);
}

#comments {
    color: #c0c0c0;
}

.rc-uid-86249 .rc-headline {
    color: #c0c0c0 !important;
}

.rc-uid-86250 .rc-headline {
    color: #c0c0c0 !important;
}
.rc-uid-86250 * h3 {
    color: #c0c0c0!important;
}
#FootW #FWI .logo {
    color: #c0c0c0;
}

/*TRENDING TODAY*/
.rc-uid-86249 * h3 {
    color: #c0c0c0!important;
}

/*MOST POPULAR*/
#DQSW ul {
    background: #252525;
}

.trifecta article h2 a {
    color: #c0c0c0;
}

article .excerpt {
    color: #c0c0c0;
}

.rc-uid-86251 .rc-headline {
    color: #c0c0c0 !important;
}

.pagination {
    background: none repeat scroll 0 0 #252525;
    background-color: rgb(37, 37, 37);
}

.top_article_main article h2 a {
    color: #c0c0c0;
}

/*ON OUR RADAR*/
#BBTrendNow h2 {
    background: #333;
    background-color: rgb(51, 51, 51);
}
#BBTrendNow ul {
    background: #333;
    background-color: rgb(51, 51, 51);
}
#BBTrendNow ul a {
    color: #c0c0c0;
}
.aList article h2 a {
    color: #c0c0c0;
}

/*"FROM THE HOMEPAGE"*/
#SideW h2 {
    color: #c0c0c0;
}
}
