css:
@-moz-document domain('7cups.com'), url-prefix('https://www.7cups.com/') {
body {
	color: #66636c;
}

#layout-wrapper, .appAd {
	background: #232226 !important;                                    
}

.dropdown-menu {
	background-color: #211e26;
}

.bg-info {
	background: #c88ec5 !important;
}

.bg-white, .badge.badge-listener {
	background-color: #000;
}

.dropdown-item {
	color: #7e7989;
}

.bg-blue-light {
	background: #27252a;
}

.widgetContent {
	background: #25242a;
    color: #87828c;
}

.text-primary {
	color: #84808f !important;
}

.module-onlinetherapyad .my-3 {
	display: none;
}

.card.card-secondary {
	background: #24202d;
}


a:focus, a:hover {
	color: #b2adb9;
}

.btn-primary {
	background: #302c36 !important;
    border-color: #3c3742 !important;
}

.btn-primary:focus {
    box-shadow: none;
}

input.form-control.form-control-underline, .input-group.input-group-underline > input.form-control {
	border-color: #615c71;

}

.gradient-footer, footer {
    background: #181a30;
	background: -moz-linear-gradient(top, #000 0%, #181a30 100%);
    background: -webkit-linear-gradient(top, #000 0%, #181a30 100%);
}


.widgetContent h1, .widgetContent h2, .widgetContent h3, .widgetContent h4, .widgetContent h5 {
	color: #685c74;
}

.form-control, .form-control:focus {
	background: #252327;
    border-color: transparent;
}

.form-control:focus {
	background: #2b292d;
    border-color: #353436;
    color: #5f5c65;
}

.note, .text-body {
	color: #b1aeb4;
}

.card {
	background: #2a272c;
}

h1, h2, h3, h4, h5 {
	color: #5d5863;
}

.activeConv, .activeConv:hover {
	background-color: #2b213b;
}

.chat-list .userImageBlock {
	width: 6em;
}

.userInfo small {
	font-size: 1.2em;
    color: #b6b1bd;
}

.chat-list {
	height: 75vh;
    overflow-y: scroll;
    scrollbar-color: #67408d transparent;
  	scrollbar-width: thin;
}

.chat-list-downbar {
	border-top: none;
}

.chat-list-downbar i, .chat-expand.open {
  background: none;
    width: 100%;
}

.chat-list-downbar i:after {
  content: 'Click here to expand the chat history :3';
}

.mt-2 {
	display: none;
}

#MessageScrollDivResize {
	scrollbar-color: #67408d transparent;
  	scrollbar-width: thin;
}

.chat-list::-webkit-scrollbar, #MessageScrollDivResize::-webkit-scrollbar {
  width: 1em;
}

.chat-list::-webkit-scrollbar-track, #MessageScrollDivResize::-webkit-scrollbar-track {
  background: transparent;
}

.chat-list::-webkit-scrollbar-thumb, #MessageScrollDivResize::-webkit-scrollbar-thumb {
  background: #67408d;
}

.chat-list::-webkit-scrollbar-thumb:hover, #MessageScrollDivResize::-webkit-scrollbar-thumb:hover {
  background: #533372;
} 

.chat-list a.list-group-item.activeConv, .chat-list a.list-group-item.activeConv:hover {
	background: #382951;
}

.chat-list .userInfo {
	width: 80%;
}

.chat-list .userInfo {
	margin-left: 4em;
}

.list-group-item {
	min-height: 13vh;
}

.bg-primary {
	background: #29223b !important;
}

.chatBox .chatInput #Comment {
	background: #1a191b;
    border-radius: 0;
}

.chatBox .chatInput {
	border-top: none;
}

.chatBox .youWrap {
	padding: 1em .65em;
    border-bottom-left-radius: 1.1em;
    background: #400f50;
    color: #ccbadb;
}

.rounded-circle, .rounded, .img-profile-l, .img-profile-t {
	border-radius: 0 !important;
}

.gradient-happy {
    background: #2b2833;
    background: -moz-linear-gradient(left, #1d1d1e 0%, #2b2833 100%);
    background: -ms-linear-gradient(left, #1d1d1e 0%, #2b2833 100%);
    background: -webkit-linear-gradient(left, #1d1d1e 0%, #2b2833 100%);
	background: linear-gradient(to right, #1d1d1e 0%, #2b2833 100%);
}

.chatBox .youImg {
	height: 4em;
    width: 4em;
}


}
