css:
@-moz-document regexp('h?t?t?p?s?:?/?/?(dev\..*|localhost|127.0.0.1)/?') {
  body {
    margin-top: 24px !important;
  }
  body::before {
  content: "Development Server";
  background: rgba(0,85,0,0.9);
  color: white;
  font-weight: bold;
  font-size:24px;
  font-family:sans-serif;
  padding: 0;
  position: fixed;
  top: 0;
  left: 50%;
  width: 100%;
  height: 24px;
  line-height: 24px;
  transform: translateX(-50%);
  z-index: 1000000;
  text-align: center;
  }
}
