css:
@-moz-document url("https://tritone.webuntis.com/WebUntis/index.do#/basic/main"), url("https://tritone.webuntis.com/WebUntis/?school=BK-Beckum#/basic/main"), url-prefix("http://webuntis.com") {
/* Un-page_body*/
 .un-page {
    background-color: #151414 !important;
}
/*un-page_header*/
 .un-page__header {
    background-color: #404040;
}
.un-empty {
    background-color: #353535!important;
}
.un-login-widget.panel form {
    background-color: #353535!important;
}
.un-dashboard-widget.panel {
    background-color: #404040 !important;
}

.untis-bootstrap .list-group-item:first-child {
    background-color: #353535!important;
}

.untis-bootstrap .list-group-item:last-child {
    background-color: #353535!important;
}

.untis-bootstrap .form-control, .untis-bootstrap [contenteditable].form-control, .untis-bootstrap [type=email].form-control, .untis-bootstrap [type=password].form-control, .untis-bootstrap [type=tel].form-control, .untis-bootstrap [type=text].form-control, .untis-bootstrap input.form-control, .untis-bootstrap input[type=email], .untis-bootstrap input[type=number], .untis-bootstrap input[type=password], .untis-bootstrap input[type=text], .untis-bootstrap textarea, .untis-bootstrap textarea.form-control {

    border-bottom: 2px solid #ff9800!important;
}

.untis-bootstrap .form-control {
    background-color: #666666 !important;
}

/* Startseite/HeutigerStundenplan/Headbar */
.untis-bootstrap .table>tfoot tr>th, .untis-bootstrap .table>thead tr>th {
    background-color: #ff9800!important;
    color: #000!important;

}
}

@-moz-document url-prefix("https://tritone.webuntis.com/WebUntis/index.do#/basic"), url-prefix("https://tritone.webuntis.com/WebUntis/?school=BK-Beckum#/basic/profile"), domain("webuntis.com") {
.un-profile-panel>.un-flex-pane {
    background-color: #353535!important;
    padding: 16px;}

.un-page {
    background-color: #151414!important;}

.un-page__header {
    background-color: #404040!important;
    display: flex!important;}

.untis-bootstrap .nav-tabs>li.active>a, .untis-bootstrap .nav-tabs>li.active>a:focus, .untis-bootstrap .nav-tabs>li.active>a:hover {
    background-color: #404040!important;}

.untis-bootstrap .nav-tabs>li>a:focus:hover, .untis-bootstrap .nav-tabs>li>a:hover {
    background-color: #404040!important;
    box-shadow: inset 0 -2px 0 #ff9800;
    color: #ff9800;}

.untis-bootstrap .nav-tabs {
    background-color: #404040!important;
    border-bottom: none;}

.untis-bootstrap .form-control {
    background-color: #666666!important;}

.untis-bootstrap .checkbox-inline label, .untis-bootstrap .checkbox label, .untis-bootstrap .radio-inline label, .untis-bootstrap .radio label {
    color: #f9f9f9!important;}

.Select-control {
    background-color: #666666!important;}

.un-scrolling-panel {
    background-color: #353535!important;}

.untis-bootstrap .modal-content {
    background-color: #151414!important;}

.un-dashboard-widget.panel .un-dashboard-widget__header--clickable:hover {
    background-color: #666666!important;}

.untis-bootstrap .form-control, .untis-bootstrap [contenteditable].form-control, .untis-bootstrap [type=email].form-control, .untis-bootstrap [type=password].form-control, .untis-bootstrap [type=tel].form-control, .untis-bootstrap [type=text].form-control, .untis-bootstrap input.form-control, .untis-bootstrap input[type=email], .untis-bootstrap input[type=number], .untis-bootstrap input[type=password], .untis-bootstrap input[type=text], .untis-bootstrap textarea, .untis-bootstrap textarea.form-control {
    border-bottom: 2px solid #ff9800!important;}

/* Nachrichten/Betreff-Absender Leiste/Schrift+Hintergrundfarbe */
.untis-bootstrap .table>tfoot tr>th, .untis-bootstrap .table>thead tr>th {
    background-color: #404040!important;
    color: #fffdfd!important;}

/* Selectleiste Hintergrundfarbe + Schriftfarbe */
.Select, .Select div, .Select input, .Select span {
    background-color: #666666!important;
    color: #000!important;}
}

@-moz-document domain("webuntis.com"), domain("tritone.webuntis.com/WebUntis/index.do#/basic/timetable") {
un-measure-it__maximize {
    background-color: #353535!important;
}

.untis-bootstrap .panel-default>.panel-heading {
    background-color: #353535!important;
}

.un-dashboard-widget.panel .panel-footer {
    background-color: #353535;
}

.un-table__group-header td {
    background-color: #353535!important;
}

.un-table__row{
    background-color: #404040!important;
}
/*Stundenplan-Header*/
.un-timetable-page__header {
    background-color: #404040!important;
}
/* Stundenplan-Hintergrund*/
.un-timetable .timetableColumnHeader, .un-timetable .timetableGridRow {
    background-color: #404040!important;
}
/*Stundenpland-Oberes linkes Quadrat*/
.un-timetable .grupetScrollContainerTopLeft {
    background-color: #404040!important;
}
/*Stundenplan-Hinter-Hintergrund*/
.un-timetable-page__body {
    background-color: #000!important;
}
/*Nur Hintergrund ohne Objekte*/
.untis-bootstrap {
    background-color: #151414!important;
}

.un-timetable {
    background-color: #151414!important;
}
/*Stundenplan-Senkrechte Trennung der Wochentage*/
.un-timetable .timetableGridColumn {
    border-right: 2px solid #151414!important;
}

/* Stundenplan/Infofenster zum Fach/Schrift */
.untis-bootstrap .panel-title>a, .untis-bootstrap .panel-title>span {
    color: #fff!important;}
.un-lesson__block-info {
    color: #fff!important;}
.modal-dialog {
    color: white!important;}

/* Stundenplan/Infofenster zum Fach/Hintergrund */
.untis-bootstrap .panel-body, .untis-bootstrap .panel-footer {
    background-color: #353535!important;}

/* Stundenplan/Infofenster zum Fach/Unterricht-Icon */
.un-unicon__icon-board-green:before {
    color: #f49f25!important;}

/* Stundenplan/Kalender/Hintergrundfarbe */
.un-datetime-picker__calendar {
    background-color: #666666!important;}

/* Stundenplan/Kalender/Schriftfarbe */
.un-datetime-picker__day {
    color: #ffffff!important;}


}
