css:
@-moz-document url-prefix("https://quire.io") {
    .tree-list {
        direction: rtl;
    }
    .pull-right {
        float: left !important;
        padding-right: 48px;
    }
    .tree-row .tree-row-front {
        float: right;
        text-align: right;
    }
    .tree-row .tree-row-front {
        text-align: left;
    }
    .tree-item.closed>.tree-row .tree-toggle:before {
        content: "\e915";
    }
    .tree-item .tree-toggle:before {
        content: "\e914";
    }
    .tree-row .i-btn:not(.available) {
        font-size: 23px;
    }
    .tree-item .tree-toggle {
        width: 23px;
    }
    .ws-sidebar .project-item .favorite-btn {
        float: right !important;
    }
}
