css:
@-moz-document domain("discordpy.readthedocs.io") {
/**
 * Dark d.py Rewrite Docs
 * IPv4#0001
 * A Discord inspired dark mode theme for the discord.py rewrite documentation
**/

/* Cleaner font */
body {
    font-family: -apple-system, system-ui, BlinkMacSystemFont, "Segoe UI", Roboto, Ubuntu;
}

/* Dark */
.indexwrapper,
body {
    background-color: #23272A; /* Dark 1 */
    color: #dcddde; /* Light 1 */
}

div.body,
.rst-versions.rst-badge .rst-current-version {
    background-color: #2C2F33; /* Dark 2 */
    color: #dcddde; /* Light 1 */
}

.active {
    background-color: #2C2F33; /* Dark 2 */
    border-left: 5px solid #2C2F33; /* Dark 2 */
}

/* Get rid of extra bgs */
tt,
code,
div.highlight {
    background-color: transparent;
}

/* Light font for dark bg */
div.sphinxsidebar a,
div.sphinxsidebar h3 a,
div.sphinxsidebar h3,
div.sphinxsidebar h4,
div.sphinxsidebar ul,
div.body h1,
div.body h2,
div.body h3,
div.body h4 {
    color: #dcddde;
}

/* Primary color */
a {
    color: #7289da;
}

a:hover {
    color: #7d96f0;
}

/* Dark bg, light font combo */
dl.describe > dt,
dl.function > dt,
dl.attribute > dt,
dl.classmethod > dt,
dl.method > dt,
dl.class > dt,
dl.exception > dt,
.container.operations > dl.describe > dt,
table.docutils thead,
table.docutils tfoot,
table.docutils thead tr th {
    background-color: #23272A; /* Dark 1 */
    color: #dcddde; /* Light 1 */
}

pre {
    background-color: #23272A; /* Dark 1 */
    color: #dcddde; /* Light 1 */
    border: 1px solid rgba(32, 34, 37, .3);
}

/* Custom colors */
div.important,
div.note,
div.hint,
div.tip,
highlight,
div.sphinxsidebar input {
    background-color: #23272A; /* Dark 1 */
    border: 1px solid #959595;
    color: #839496;
}

div.attention,
div.warning,
div.caution,
div.seealso {
    background-color: #665623;
}

div.helpful {
    background-color: #162f44;
    border: 1px solid #4478ac;
}

div.danger,
div.error {
    background-color: #401414;
    border: 1px solid #f66;
}

.container.operations::before {
    color: #dcddde; /* Light 1 */
}

.highlight .k,
.highlight .ow,
.highlight .nb,
.highlight .kc,
.highlight .bp {
    color: #00ae32;
}

.highlight .nf {
    color: #5183ff;
}

.highlight .hll {
    background-color: #0076ff30;
}

/* Rounding */
div.body,
div.admonition,
dl.describe > dt, dl.function > dt, dl.attribute > dt, dl.classmethod > dt, dl.method > dt, dl.class > dt, dl.exception > dt,
.container.operations,
.rst-versions.rst-badge .rst-current-version,
.active,
pre,
img {
    border-radius: 5px;
}

div.sphinxsidebar #searchbox input[type="text"] {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
}

div.sphinxsidebar #searchbox input[type="submit"] {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
}

dl.describe > dt, dl.function > dt, dl.attribute > dt, dl.classmethod > dt, dl.method > dt, dl.class > dt, dl.exception > dt {
    padding: 2px 10px;
}

/* Scrolling: Firefox */
body {
    scrollbar-width: 10px;
    scrollbar-color: #2C2F33 #23272A; /* Thumb Track */
}

/* Scrolling: Chrome etc */
::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-track {
    background: #23272A; /* Dark 1 */
}

::-webkit-scrollbar-thumb {
    background: #2C2F33; /* Dark 2 */
    border-radius: 5px;
}

::-webkit-scrollbar-thumb:hover {
    background: #2C2F33; /* Dark 2 */
}

div.sphinxsidebarwrapper {
    padding-right: 10px;
    box-sizing: border-box;
}

div.sphinxsidebarwrapper:hover {
    padding-right: 0;
    overflow-y: scroll;
}

/* Dark landing image */
#welcome-to-discord-py img {
  content: url("https://i.imgur.com/wP1DGqL.png");
}
}
