css:
@-moz-document regexp("(.*)bakalari.cz/bakaweb/next/login(.*)"), regexp("(.*)bakalari.cz/bakaweb/next/serpwd(.*)") {
/* Přihlašovací stránka */

#info {filter: invert(95%)}

body {
    background: rgb(20,20,20) !important;
}
#loginBox {
    background: rgb(40,40,40);
    opacity: 0.90
}

button, #pwdcallback_ButtonMail {background: -moz-linear-gradient(-90deg, rgb(220,80,20), rgb(180,80,20)) !important; border-color: rgb(200,80,20) !important; box-shadow: 0px 0px 3px 0px rgb(200,80,20) !important; color: rgb(20,20,20) !important; font-weight: bold}

#popupintro_PW-1 {border-color: rgb(15,15,15) !important}
#popupintro_PWC-1 {background-color: rgb(28,28,28) !important; color: rgb(150,150,150)}
}

@-moz-document domain("bakalari.cz") {
.dxICheckBox_NextBlueTheme {filter: invert(100%)}
.myButton, .myButton2 {border-color: rgb(200,80,20) !important; font-weight: bold}
.myButton {background: rgb(20,20,20); color: rgb(200,80,20)}
.myButton2 {background: rgb(200,80,20); color: rgb(10,10,10)}
.myButton:hover {background: rgb(25,25,25)}
.myButton2:hover {background: rgb(200,100,20)}

.nav {
    background-color: rgb(15,15,15) !important;
}
#topheader {
    background-color: rgb(20,20,20);}
#info-uzivatel, .info-user-resp {
    background-color: rgb(15,15,15) !important;
}

header, header.uvod {
    background: rgb(15,15,15);
    border-color: rgb(15,15,15);
}

#panel {
    background: rgb(10,10,10);
}
.cara {background: -moz-linear-gradient(180deg, rgb(10,10,10), rgb(5,5,5)) !important; background-image: none}

.level_Basic > a:hover, .level_Premium > a:hover {
    background: rgb(222,110,20) !important;
    color: rgb(13,13,13) !important;
    font-weight: bold;
}

.selected > a {
    background: rgb(222,110,20) !important;
    color: rgb(12,12,12) !important;
    font-weight: bold;
}


#obsah {
    background: rgb(20,20,20);
    color: rgb(150,150,150);
}
h1 {color: rgb(200,200,200) !important}
h2 {color: rgb(180,180,180) !important}

.user {background-color: rgb(15,15,15) !important}
.positionable ul .fl {background-color: rgb(20,20,20); border-color: rgb(15,15,15)}
.positionable ul .fl:hover * {background-color: rgb(15,15,15)}



/* Filtr  */
.dxeButtonEditSys {border: rgb(200,80,20)}
.dxic, input {background: rgb(10,10,10)!important; border: 2px solid rgb(200,80,20); color: rgb(150,150,150) !important}
.dxeButton {filter: invert(100%)}

}

@-moz-document regexp("(.*)bakalari(.*)dash.aspx") {
.uvod-text::after {background: rgb(15,15,15) !important}

div[id*=cphmain_] table tbody tr td img {filter: invert(100%)}
div[id*=cphmain_] table {border-color: rgb(200,80,20) !important}

.dxdpLite_NextBlueTheme {background: rgb(20,20,20) !important;}
.dxpc-mainDiv {background: rgb(20,20,20) !important;}
.dxpc-header {background: rgb(20,20,20) !important;}
.buttons, .button {filter: invert(100%)}
.frame {border-color: rgb(10,10,10)}
.dxdz-pnlPlcHolder {background-color: rgb(20,20,20) !important; border-color: rgb(15,15,15) !important}


/* Klasifikace a Upozornění */
tbody tr td {background: rgb(10,10,10)}
tbody tr td * {color: rgb(200,200,200)}
tr:nth-of-type(even) * {background-color: rgba(15,15,15,0.999) !important}
#cphmain_pprubznamky_prubZnaDiv table tbody tr td:not(:nth-child(2)) {color: rgb(150,150,150) !important}
.cervena td > a {color: rgb(200,80,20) !important}
.cervena td:nth-child(2) {color: rgb(200,80,20) !important}


/* Kalendář */
.dxeCalendar_NextBlueTheme tbody tr td {background-color: rgb(15,15,15) !important}
.dxeCalendarHeader_NextBlueTheme {border-bottom: 2.2px solid #090909}
.dxeCalendarDayHeader_NextBlueTheme {color: rgb(100,100,100) !important; border-bottom: 0px}
.dxeCalendarWeekNumber_NextBlueTheme {color: rgb(100,100,100) !important; font-size: 0.9em}
.dxeCalendarToday_NextBlueTheme {font-weight: bold; border: 2px solid rgb(200,80,20); color: rgb(200,80,20)}
.dxeCalendarWeekend_NextBlueTheme {color: rgb(200,80,20)}
.dxeCalendarOtherMonth_NextBlueTheme {color: rgb(100,100,100)}
.dxe img {filter: invert(100%)}


/* Domácí úkoly */
.cellpozde {color: rgb(200,80,20)}
.cellnad7 {color: rgb(150,150,150)}


/* Rozvrh dne */
#RozvrhAktualni {color: rgb(50,50,50)}
.navheader {background-color: rgb(20,20,20)}
#fr4 .frame_body table tbody tr:nth-of-type(2n-1) th {background-color: rgb(10,10,10) !important}
#fr4 .frame_body table tbody tr th {color: rgb(200,80,20) !important}
#fr4 .frame_body table tbody tr th {color: rgb(200,80,20) !important}
#fr4 .frame_body table tbody tr:not([class=pink]) td:nth-of-type(1) {color: rgb(200,200,200) !important}
#fr4 .frame_body table tbody tr:not([class=pink]) td:not(:nth-of-type(1)) {color: rgb(120,120,120) !important}

.pink * {color: rgb(200,80,20) !important}


/* Suplování */
.cervena2 {color: rgb(200,80,20)}
#fr5 {color: white !important}


/* Plán akcí */
/*    ???    */


/* Aktuality */
/*    ???    */



}

@-moz-document regexp("(.*)osobni_udaje.aspx") {
header {background: rgb(10,10,10); border-color: rgb(10,10,10)}
#cphmain_PanelUdaje_Pages {background: rgb(10,10,10) !important}

.zalozka-aktivni {color: rgb(200,200,200) !important; background: rgb(20,20,20) !important}
.zalozka {color: rgb(150,150,150); background: rgb(20,20,20) !important}
.spacer {background: rgb(15,15,15) !important}
.stranka {background: rgb(20,20,20) !important; border: 0px !important}
}

@-moz-document regexp("(.*)prubzna.aspx(.*)") {
.rozjed {filter: invert(100%)}
.item-down-cover {background: rgba(0,0,0,0.0) !important}
.predmet-radek, .znamka-v {background-color: rgb(20,20,20) !important}

.header_ikony {filter: invert(100%)}

/* Filtr */
.dxpcLite_NextBlueTheme.dxpc-mainDiv {border-color: rgb(10,10,10) !important}
#pagePopups_popupfiltr_PWC-1 {background: rgb(15,15,15) !important; border-color: rgb(10,10,10) !important}

.pop h3 {color: rgb(180,180,180)}
.pop a {color: rgb(200,80,20) !important}


/* Řazení "Předměty" */
.obal h3, .dalsi-dva div {color: rgb(200,200,200) !important}
.hlavni {filter: invert(100%)}
.bx-prev, .bx-next {filter: invert(100%)}

div .ob {color: rgb(130,130,130)}
.cervena .ob {color: rgb(250,15,10)}
.znamka-v:hover {font-weight: bold; transition: 500ms; background: rgba(200,200,200,0.1) !important}

#pagePopups_popupdetail_PW-1 * {background: rgb(10,10,10) !important; color: rgb(180,180,180)}
h4 {color: rgb(200,200,200) !important; font-weight: bold}
.cervena div {color: rgb(200,80,20) !important}

/* Řazení Chronologicky */
thead tr th {background: rgb(20,20,20) !important; color: rgb(200,200,200)}
.table_classic tbody tr:nth-of-type(2n-1) {background: rgb(10,10,10) !important}
td {border-color: black !important}
.table_classic tbody tr td:nth-of-type(1) {color: rgb(200,200,200) !important}
.table_classic tbody tr td:nth-of-type(2) {color: rgb(200,200,200) !important}
.table_classic tbody tr .cervena:nth-of-type(2) {color: rgb(250,15,10) !important}
.table_classic tbody tr td:nth-of-type(3), tbody tr td:nth-of-type(4), tbody tr td:nth-of-type(7) {color: rgb(140,140,140) !important}


/* Filtr */
.dxeCalendarToday_NextBlueTheme {border-color: rgb(200,80,20) !important; color: rgb(200,80,20) !important}
.dxeCalendarDay_NextBlueTheme {color: rgb(100,100,100)}
.dxeCalendarSelected_NextBlue_Theme {color: rgb(200,200,200) !important; font-weight: bold}
.dxeCalendarWeekend_NextBlueTheme {color: rgb(250,15,10)}

#pagePopups_popupfiltr_dateod_DDD_C {background: rgb(20,20,20); border-color: rgb(200,80,20)}
#pagePopups_popupfiltr_dateod_DDD_C tbody tr td {background: rgb(20,20,20)}
.dxeCalendarHeader_NextBlueTheme table tbody td img {filter: invert(100%)}
#pagePopups_popupfiltr_dateod_DDD_C_T {color: rgb(180,180,180)}
#pagePopups_popupfiltr_dateod_DDD_C_BT {background: rgb(200,80,20) !important; box-shadow: 1px 1px 1px rgb(120,30,10) !important; border-color: rgb(120,30,10) !important}

}

@-moz-document regexp("(.*)pololetni.aspx(.*)"), regexp("(.*)vychovna.aspx(.*)"), regexp("(.*)opravne.aspx(.*)") {
/* Známky */
.table_classic thead tr th {background: rgb(10,10,10) !important; color: rgb(150,150,150) !important; border-color: rgb(0,0,0)}
.table_classic tbody tr:nth-of-type(2n-1) td {background: rgb(20,20,20)}
.table_classic tbody tr:nth-of-type(2n) td {background: rgb(10,10,10)}
.table_classic {border: 2px solid rgb(10,10,10)}
.table_classic td {border-top: 0px !important;}

.hlavicka_radky {color: rgb(170,170,170) !important}
.prvni {color: rgb(170,170,170)}
.druhy {color: rgb(200,80,20) !important}



/* Souhrny */
#cphmain_divSouhrny table thead tr th {background: rgb(10,10,10) !important}
#cphmain_divSouhrny table {border: 2px solid rgb(10,10,10)}
#cphmain_divSouhrny table tbody tr td {color: rgb(150,150,150) !important}
.color {background-color: rgb(15,15,15)}


/* Výchovná opatření */
.maly_text {color: rgb(180,180,180)}
.modry_text {color: rgb(200,80,20) !important}



/* Opravné zkoušky */
/*       ???       */

}

@-moz-document regexp("(.*)rozvrh.aspx(.*)") {
.fa-info-circle {filter: invert(100%)}
.dropdown-content {background-color: rgb(20,20,20) !important; border-color: rgb(200,80,20) !important; color: rgb(180,180,180)}
.dxeButton {filter: invert(100%)}
#cphmain_datum {border-color: rgb(200,80,20)}
.dxic, .dxic input {background: rgb(10,10,10) !important; color: rgb(180,180,180) !important}

/* Rozvrh */
.clearfi {background: rgb(20,20,20)}
#hours {background: rgb(10,10,10)}
#hours .num {color: rgb(200,80,20) !important}
#hours .hour {color: rgb(180,180,180) !important}

.left {color: rgb(200,80,20) !important}
.right {color: rgb(180,180,180) !important}

.day-item-hover:not(.green) {background: rgb(10,10,10) !important; transition: 400ms}
.day-item-hover:hover {background: rgb(13,13,13) !important}
.day-name {background: rgb(10,10,10); color: rgb(180,180,180) !important}
.day-item, .empty {border: 2px solid rgb(20,20,20) !important}
.clearfix:not(.top) {background: rgb(20,20,20) !important}
.empty {background: rgb(13,13,13) !important; height: 84.5px !important}
.green {border-left: 2px solid rgb(0,80,0) !important}
.green div {background: rgb(0,130,0) !important; border: none; transition: 400ms}
.green div .middle {color: rgb(10,10,10) !important}
.green:hover div {background: rgb(0,110,0) !important}
.day-item:not(.pink) .day-item-hover div .middle:not(.zapsano) {color: rgb(200,200,200) !important}
}
