css:
@-moz-document url("https://sspai.com/") {
body{background-color:transparent}
.article-card{border:none;box-shadow:none}
header#header .fixed{display:none}
header#header{ height:0}
#app-head,.main-banner-box,.minor-banner-box,.index_right,.swiper-container,#home .side,.img_box{ display:none}
#home .main{float:none; margin:auto; }
.advertisementCardBox .card_content{width:100% !important}
.home-page .contain-box{ max-width:720px  !important}

#home > div.container > div.main > div:nth-child(4)
{
display:flex;
    flex-flow:row wrap;
    justify-content:stretch;
    align-items:flex-start;
}
#home > div.container > div.main > div:nth-child(4) .article-card{
flex:0 1 50%
}
#home .main{width:1200px}

.main > .article-card{
    display: inline-block;
    width: 555px;
    margin-left: auto;
    margin-right: auto;
    }

.advertisementCardBox .card_content .title{ height:auto  !important}
.recommend-card{ display:none;}


#app{background:#ffffff !important;padding-top:0 !important;}
}

@-moz-document url-prefix("https://sspai.com/post/") {
.header .fixed{ position:relative !important}
}
