css:
@-moz-document url-prefix("https://stygians.thesporkedken.com") {
body {
    background-color: #bac0d6;
	
}

a, a:hover, a:focus, a:visited {
    color: #9395bb;
}

a:link {
    color: #182435;
}

.navbar-custom {
    background-color: #090b23;
    border-bottom: 1px solid #9395bb;
}

nav.navbar-default {
    background-color: #9395bb;
    border-color: #9395bb;
}

nav.navbar-default>.container>.navbar-header {
    color: #182435;
    font-weight: bold;
}

nav.navbar-default .navbar-brand, nav.navbar-default .navbar-nav>li>a {
    color: #182435;
}

nav.navbar-default .navbar-brand:focus, nav.navbar-default .navbar-brand:hover, nav.navbar-default .navbar-nav>li>a:focus, nav.navbar-default .navbar-nav>li>a:hover {
    color: #404872;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover {
    background-color: #bac0d6;
    color: #182435;
}

.jumbotron {
    background-color: #dce2f6;
    color: #182435;
    border: 1px solid #9395bb;
    border-radius: 5px;
}

.panel, .panel-default, .panel-default>.panel-heading {
    background-color: #dce2f6;
    border-color: #9395bb;
    color: #404872;
}

.panel-default>.panel-heading {
    background-color: #dce2f6;
    font-size: 14px;
    font-weight: bold;
}

hr {
    border-top: 1px solid #9395bb;
}

.dropdown-menu, .dropdown-menu>li>a:hover {
    background-color: #dce2f6;
}

.dropdown-menu>li>a, .dropdown-menu>li>a:hover {
    color: #182435;
}

.dropdown-menu>li>a:hover {
    font-weight:bold;
}

.btn-secondary {
    background-color: #dce2f6;
    border: 1px solid #9395bb;
    border-radius: 5px;
}

.btn-active {
    background-color: #41a1d8;
    border: 1px solid #9395bb;
    border-radius: 5px;
    color: #000;
    font-weight: bold;
}

.btn-active a{
    color: #000;
    font-weight: bold;
}

.alert {
    color: #182435;
    font-weight: bold;
}

.alert-message {
    background-color: #000;
}

.alert-error {
    background-color: #333;
}

.modal-header {
    background-color: #dce2f6;
    border: 1px solid #9395bb;
    border-bottom: none;
    border-radius: 5px 5px 0 0;
}

.modal-body {
    background-color: #090b23;
    border: 1px solid #9395bb;
}

.text-bubble {
    background-color: #dce2f6;
    border: 1px solid #9395bb;
    border-radius: 5px;
    padding: 6px 12px;
    margin-right: 5px;
}

input[type=text], textarea.form-control {
    background-color: #dce2f6;

	border: 2px solid 182435;
	color: 182435;
}

select.form-control, option {
    background-color: #dce2f6;
    color: 182435;
    border: 2px solid 182435;
}

.panel-icon {
    margin-right: 5px;
}
.pagination {
    float: right;
}
.pagination li a, .pagination>.disabled>span {
    background-color: #dce2f6;
    border-color: #9395bb;
}
.pagination>.active>span {
    background-color: #dce2f6;
    border-color: #9395bb;
    color: #000;
}
.page-header-nav li {
    float:none;
    display:inline-block;
}
.panel-blurb {
    margin-bottom: 1.3em;
}
.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-top: 1px dotted #182435;
}

.boss-stat-group {
    margin-bottom: 15px;
}


div.alert.alert-success {
    background-color: #dbe1f5;
    border: 1px solid #182435;
}

.pet-block {
    margin-bottom: 15px;
    display: inline-block;
}
.pet-block .pet-image {
    margin-bottom: 5px;
    max-width: 100%;
}
.box-header { display: block; }

.pet-main-panel .edit-pet { margin-left: 10px; }

.dex-item { display: inline-block; }
.dex-item .dex-image { max-width: 100%; margin-bottom: 10px; }
.dex-item .dex-info { display: table; width: 100%; }
.dex-item .dex-name-wrapper { 
    display: table-row;
    margin: 0px auto;
}
.dex-item .dex-name-wrapper .dex-owned { display: table-cell; padding-right: 5px; width: 20px; }
.dex-item .dex-name-wrapper .dex-name {
    display: table-cell;
    font-weight: bold; 
    background-color: #dce2f6;
    border: 1px solid #182435; 
    border-radius: 5px;
    padding: 2px 10px;
}

.pet-growth-line {
    display: flex;
    align-items: center;
    justify-content: center;
}
.pet-growth-line .stage-block {
    display: inline-block;
}
.pet-growth-line .stage-block > img {
    margin-bottom: 10px;
}
.pet-growth-line .next-arrow {
    display: inline-block;
    margin-left: 20px;
    margin-right: 20px;
    text-align: center;
}
.pet-growth-line .stage-block-wrapper { 
    display: flex; 
    align-items: center;
    justify-content: center;
}
.pet-growth-line .stage-block-wrapper:after { /* for the dex, inserts arrow automatically */
    content: '\25b6';
    display: inline-block;
    margin-left: 20px;
    margin-right: 20px;
    text-align: center;
}
.pet-growth-line .stage-block-wrapper:last-of-type:after { 
    display: none;
}

.list-group>.list-group-item {
    background-color: #dce2f6;
    font-size: 14px;
    font-weight: bold;
    border-color: #182435;
}

p { color: #182435; }

.alert.alert-warning {
    color: #090b23;
}

.alert.alert-warning a {
    color: #9395bb;
}

.row.stygian-stats {
	padding: 0px 25px;
}
.row.stygian-stats .col-xs-3 {
	padding: 0px;
}

.table-responsive { border: 0px; }

.constrain-width { max-width: 100%; }

.card-wrapper {
    margin-bottom: 20px;
}
.card-image {
    margin-bottom: 10px;
    display: block;
}
.card-name {
    margin-bottom: 3px;
    display: block;
}
.card-count {
    margin-bottom: 10px;
    display: block;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: rgba(255, 255, 255, 0.05);
}

.requests-table>tbody>tr>td { padding: 15px; }
.requests-table .request { border-top: 1px solid #9395bb; }
.requests-table .request .request-header { margin: 0px; }
.requests-table>tbody>.request-content>td { padding-top: 20px; }
.requests-table .request-content .stygian {
    margin-bottom: 10px;
}
.requests-table .request-content .stygian .placeholder-image {
    margin-left: auto;
    margin-right: auto;
    width: 200px;
    height: 193px;
    background-color: #dce2f6;
    line-height: 193px;
    border-radius: 5px;
}
.requests-table .request-content hr {
    border-top: 1px solid #182435; 
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: rgba(255, 255, 255, 0.3);
}

.well {
    background-color: #dce2f6;
    border-color: #182435;
    color: #182435;
}
.form-group label {
    color: #182435;
}
.form-group .form-control-static {
    color: #404872;
}
.modal-content h3 {
    color: #404872;
}

/* used on my stygians page */
.stygian-row .stygian-name {
    font-weight: bold; 
    background-color: #dce2f6;
    border: 1px solid #182435; 
    border-radius: 5px;
    padding: 2px 10px;
    margin-bottom: 10px;
}

/* pots */

.pot-block {
    min-height: 50px;
    margin-bottom: 10px;
}

/* notifications */

.new-notification {
    background-color: rgba(255, 255, 255, 0.1);
}

/* dungeons */
.cards-area .card-highlight {
    padding: 10px;
}
.cards-area .card-highlight.selected {
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 10px;
}
blockquote.mutated {
    border-left: 5px solid #f00;
}
.mutation-alert {
    color: #f00;
    font-weight: bold;
}
}
